            cube([20, 15, 6], center=true);

        // Rails
        for(y = [-6, 6])
            translate([0, y, -4])
                cube([25, 2, 2], center=true);
    }
}

module detector() {
    translate([0, -25, 20]) {
        // Screen
        cube([20, 2, 15], center=true);

        // Screen frame
