button_height = 1;

// Create the main body
module main_body() {
    difference() {
        // Outer shell
        color(color_case)
        cube([body_length, body_width, body_height], center=true);

        // Hollow out the inside
        translate([0, 0, 0])
        cube([body_length - wall_thickness*2, body_width - wall_thickness*2, body_height - wall_thickness*2], center=true);
    }
}

// Display screen
module display_screen() {
    translate([body_length/2 - screen_depth - 5, 0, body_height/2 - screen_height/2 - 2])
    color(color_screen)
    cube([screen_depth, screen_width, screen_height], center=false);

    // Screen bezel
    translate([body_length/2 - screen_depth - 6, 0, body_height/2 - screen_height/2 - 3])
    color(color_case)
    cube([screen_depth + 2, screen_width + 4, screen_height + 4], center=false);
}

// Optical lens assembly
module lens_assembly() {
    translate([-body_length/2 + lens_length/2 + 5, 0, 0])
    union() {
        // Lens tube
        color(color_case)
        cylinder(h=lens_length + 2, r1=lens_diameter/2 + 3, r2=lens_diameter/2 + 2, center=true);

        // Lens
        translate([0, 0, 0])
        color(color_lens)
        cylinder(h=lens_length, r=lens_diameter/2, center=true);

        // Lens holder ring
        translate([lens_length/2 + 1, 0, 0])
        color(color_case)
        cylinder(h=2, r=lens_diameter/2 + 3, center=true);
    }
}

// Diffraction grating module
module diffraction_grating() {
    translate([-body_length/2 + lens_length + 15, 0, 0])
    rotate([0, 90, 0])
    color(color_grating)
    cube([grating_thickness, grating_width, grating_height], center=true);

    // Grating holder
    color(color_case)
    translate([-body_length/2 + lens_length + 15, 0, 0])
    difference() {
        cube([grating_thickness + 4, grating_width + 6, grating_height + 6], center=true);
        cube([grating_thickness + 5, grating_width + 2, grating_height + 2], center=true);
    }
}

// Control buttons
module control_buttons() {
    // Power button
    translate([body_length/2 - 10, -body_width/2 + button_diameter/2 + 3, body_height/2 - button_height/2 - 2])
    color(color_buttons)
    cylinder(h=button_height, r=button_diameter/2, center=true);

    // Mode button
    translate([body_length/2 - 10, 0, body_height/2 - button_height/2 - 2])
    color(color_buttons)
    cylinder(h=button_height, r=button_diameter/2, center=true);

    // Calibrate button
    translate([body_length/2 - 10, body_width/2 - button_diameter/2 - 3, body_height/2 - button_height/2 - 2])
    color(color_buttons)
    cylinder(h=button_height, r=button_diameter/2, center=true);
}

// Grips on the sides
module side_grips() {
    // Left grip
    translate([0, -body_width/2 - 1, 0])
    color(color_case)
    for(i = [-body_length/2 + 10 : 10 : body_length/2 - 10]) {
        translate([i, 0, 0])
        cube([8, 2, body_height - 4], center=true);
    }

    // Right grip
    translate([0, body_width/2 + 1, 0])
    color(color_case)
    for(i = [-body_length/2 + 10 : 10 : body_length/2 - 10]) {
        translate([i, 0, 0])
        cube([8, 2, body_height - 4], center=true);
    }
}

// Assembly
