    color("black") legs();
}

// Base with viewing windows
module base() {
    difference() {
        // Main base block
        cube([base_width, base_depth, base_height], center=true);

        // Viewing windows
        for (i = [-1, 0, 1]) {
            translate([i * window_spacing, 0, base_height/2 - 1])
                cylinder(h=2, r=window_diameter/2, center=true);
        }

        // Sample chamber
        translate([0, 0, 0])
            cube([slide_width + 4, slide_depth + 2, slide_height + 2], center=true);
    }
}

// Sliding color filter holder
module slide() {
    translate([0, 0, (base_height + slide_height)/2 + 0.5])
    difference() {
        // Main slide block
        cube([slide_width, slide_depth, slide_height], center=true);

        // Filter windows
        for (i = [-1, 0, 1]) {
            translate([i * window_spacing, 0, 0])
                cylinder(h=slide_height + 1, r=window_diameter/2, center=true);
