    translate([base_width/2 + handle_length/2, 0, 0])
    rotate([0, 90, 0])
    difference() {
        // Main handle cylinder
        cylinder(h=handle_length, r=handle_diameter/2, center=true);

        // Grip texture
        for (i = [0:3]) {
