// Color definitions
glass_color = [0.8, 0.9, 1.0, 0.6];
scale_color = [0.2, 0.2, 0.2];
weight_color = [0.4, 0.4, 0.4];

// Main body module
module densimeter_body() {
    color(glass_color) {
        difference() {
            // Outer shell
            union() {
                // Stem
                translate([0, 0, bulb_length])
                    cylinder(h = stem_length, d = stem_diameter, center = false);

                // Bulb
                translate([0, 0, 0])
                    sphere(d = bulb_diameter);

                // Transition cone
                translate([0, 0, bulb_length])
                    cylinder(h = 5, d1 = bulb_diameter, d2 = stem_diameter, center = false);
            }

            // Hollow interior
            translate([0, 0, -2])
                sphere(d = bulb_diameter - wall_thickness*2);

            translate([0, 0, bulb_length])
                cylinder(h = stem_length + 5, d = stem_diameter - wall_thickness*2, center = false);
        }
    }
}

// Scale markings on stem
module scale_markings() {
    color(scale_color) {
        for(i = [0:scale_markings-1]) {
            // Main markings
            translate([0, stem_diameter/2 + 0.2, bulb_length + stem_length*i/(scale_markings-1)])
                cube([0.5, 0.2, 1], center = true);

            // Number indicators
            translate([0, stem_diameter/2 + 0.8, bulb_length + stem_length*i/(scale_markings-1)])
                cube([2, 0.2, 0.2], center = true);
        }

        // 1.000 reference line
        translate([0, stem_diameter/2 + 0.2, bulb_length + stem_length*0.4])
            cube([1.2, 0.3, 1.5], center = true);
    }
}

// Weight at bottom
module bottom_weight() {
    color(weight_color) {
        difference() {
            // Main weight
            translate([0, 0, -bulb_length/2 - 3])
                sphere(d = bulb_diameter - 2);

            // Hollow section
            translate([0, 0, -bulb_length/2 - 3])
                sphere(d = bulb_diameter - 6);
        }

        // Weight retaining ring
        translate([0, 0, -2])
            cylinder(h = 2, d = bulb_diameter + 2, d1 = bulb_diameter + 2, d2 = bulb_diameter, center = false);
    }
}

// Top loop/handle
module top_handle() {
    color(glass_color) {
        // Handle ring
        translate([0, 0, bulb_length + stem_length + 3])
            rotate([90, 0, 0])
                torus(stem_diameter/3, stem_diameter/2);
    }
}

// Helper module for torus (handle ring)
module torus(r, tube_r) {
    rotate_extrude()
        translate([r, 0, 0])
            circle(tube_r);
}

