// Lactometer (Milk Hydrometer) - Medium Complexity Realistic Design
// This lactometer includes a weighted bulb, graduated stem, and calibration marks

$fn = 100;

// Overall dimensions
stem_length = 80;
stem_diameter = 8;
bulb_length = 35;
bulb_diameter = 18;
weight_cavity = 12;
scale_markings = 15;

// Colors
color_milk = [0.95, 0.95, 0.9];
color_glass = [0.8, 0.9, 1, 0.6];
color_marks = [0.1, 0.1, 0.1];
color_weight = [0.5, 0.5, 0.5];

module main_body() {
    // Glass bulb with weighted bottom
    difference() {
        // Main bulb
        color(color_glass)
        union() {
            // Spherical bulb
            translate([0, 0, bulb_length/2])
            sphere(d=bulb_diameter);

            // Cylindrical neck transition
            translate([0, 0, bulb_length])
            cylinder(d1=bulb_diameter, d2=stem_diameter, h=5);
        }

        // Hollow interior
        translate([0, 0, -5])
        sphere(d=bulb_diameter-2);

        // Weight cavity
        translate([0, 0, bulb_length/4])
        cylinder(d=weight_cavity, h=weight_cavity/2);
    }

    // Stem
    color(color_glass)
    translate([0, 0, bulb_length+5])
    cylinder(d=stem_diameter, h=stem_length);
}

module calibration_marks() {
    // Graduation marks on stem
    for(i=[0:scale_markings-1]) {
        height = (i * (stem_length-10) / scale_markings) + bulb_length + 10;
        mark_length = (i % 5 == 0) ? 6 : 3;
        mark_width = (i % 5 == 0) ? 1.5 : 1;

        color(color_marks)
        translate([stem_diameter/2 + mark_length/2, 0, height])
        cube([mark_length, mark_width, 0.5], center=true);

        // Numbers every 5th mark
        if(i % 5 == 0 && i > 0) {
            translate([stem_diameter/2 + 10, 0, height-2])
            linear_extrude(0.5)
            text(str(i*5), size=3, halign="center", valign="center");
        }
    }
}

module weight_ballast() {
    // Lead weight in bulb base
    color(color_weight)
    translate([0, 0, bulb_length/4])
    cylinder(d=weight_cavity-1, h=weight_cavity/2-1);
}

module top_cap() {
    // Protective top cap
    color(color_marks)
    translate([0, 0, bulb_length+stem_length+5])
    cylinder(d=stem_diameter+2, h=3);

    // Hanging loop
    translate([0, 0, bulb_length+stem_length+8])
    rotate([90, 0, 0])
    difference() {
        torus(stem_diameter/2+3, 1.5);
        translate([0, 0, -1.6])
        cube([20, 3, 3], center=true);
    }
}

// Helper module for torus
module torus(r, thickness) {
    rotate_extrude()
    translate([r, 0])
    circle(thickness);
}

// Assembly
union() {
    main_body();
    weight_ballast();
    calibration_marks();
    top_cap();
}

// Alcoholmeter - Realistic Style, Medium Complexity
// A hydrometer for measuring alcohol content in spirits

// Parameters
$fn = 60;
scale_length = 70;
scale_diameter = 8;
bulb_diameter = 22;
bulb_length = 35;
stem_length = 20;
stem_diameter = 6;
weight_length = 15;
weight_diameter = 18;
weight_wall = 2;

// Main module
difference() {
    // Main body assembly
    union() {
        // Glass bulb
        color([0.9, 0.95, 1, 0.7]) {
            // Main bulb
            translate([0, 0, (scale_length + stem_length) * 0.5]) {
                cylinder(h = bulb_length, d1 = bulb_diameter, d2 = bulb_diameter * 0.9);
            }
            // Rounded bottom
            translate([0, 0, (scale_length + stem_length) * 0.5 - bulb_length * 0.5]) {
                sphere(d = bulb_diameter);
            }
            // Transition to stem
            translate([0, 0, (scale_length + stem_length) * 0.5 + bulb_length * 0.5]) {
                cylinder(h = stem_length, d1 = bulb_diameter * 0.9, d2 = stem_diameter);
            }
        }

        // Scale tube (upper part)
        color([0.9, 0.95, 1, 0.7]) {
            translate([0, 0, stem_length + (scale_length + stem_length) * 0.5 + bulb_length * 0.5]) {
                cylinder(h = scale_length, d = scale_diameter);
            }
        }

        // Weight chamber (bottom)
        color([0.7, 0.7, 0.7]) {
            translate([0, 0, (scale_length + stem_length) * 0.5 - bulb_length * 0.5 - weight_length * 0.5]) {
                difference() {
                    cylinder(h = weight_length, d = weight_diameter);
                    cylinder(h = weight_length + 2, d = weight_diameter - weight_wall * 2);
                }
            }
        }

        // Measurement scale markings (0-100%)
        for(i = [0:10:100]) {
            color([0, 0, 0]) {
                translate([scale_diameter * 0.5 + 0.1, 0, 
                    stem_length + (scale_length + stem_length) * 0.5 + bulb_length * 0.5 + scale_length * (i/100)]) {
                    cube([1, 0.2, 0.5]);
                }
                if(i % 20 == 0) {
                    translate([scale_diameter * 0.5 + 1.5, 0, 
                        stem_length + (scale_length + stem_length) * 0.5 + bulb_length * 0.5 + scale_length * (i/100)]) {
                        cube([2, 0.2, 1]);
                    }
                }
            }
        }

        // Paper scale insert
        color([1, 1, 0.9]) {
            translate([0, 0, stem_length + (scale_length + stem_length) * 0.5 + bulb_length * 0.5 + scale_length * 0.5]) {
                rotate([90, 0, 0]) {
                    cylinder(h = scale_length - 2, d = scale_diameter - 1.5);
                }
            }
        }
    }

    // Hollow out the glass parts
    color([0.9, 0.95, 1, 0.7]) {
        // Main bulb hollow
        translate([0, 0, (scale_length + stem_length) * 0.5]) {
            cylinder(h = bulb_length + 5, d = bulb_diameter - 2);
        }
        translate([0, 0, (scale_length + stem_length) * 0.5 - bulb_length * 0.5 - 2]) {
            sphere(d = bulb_diameter - 2);
        }

        // Stem hollow
        translate([0, 0, (scale_length + stem_length) * 0.5 + bulb_length * 0.5 + stem_length * 0.5]) {
            cylinder(h = stem_length + 5, d = stem_diameter - 1.5);
        }

        // Scale tube hollow
        translate([0, 0, stem_length + (scale_length + stem_length) * 0.5 + bulb_length * 0.5 + scale_length * 0.5]) {
            cylinder(h = scale_length + 2, d = scale_diameter - 2);
        }
    }
}

// Reinforcement ring at bulb-stem junction
color([0.8, 0.8, 0.8]) {
    translate([0, 0, (scale_length + stem_length) * 0.5 + bulb_length * 0.5]) {
        cylinder(h = 2, d = bulb_diameter * 1.1, center = true);
    }
}

// Top safety cap
color([0.9, 0.9, 0.9]) {
    translate([0, 0, stem_length + (scale_length + stem_length) * 0.5 + bulb_length * 0.5 + scale_length + 1]) {
        cylinder(h = 3, d = scale_diameter + 1, center = true);
    }
}

