// Salinometer - Medium complexity realistic style
// A hydrometer-style tool for measuring salt concentration in water

$fn = 32;

// Main dimensions
body_length = 80;
body_diameter = 12;
bulb_diameter = 18;
stem_length = 50;
scale_length = 30;

// Colors
glass_color = [0.9, 0.95, 1, 0.7];
scale_color = [0.2, 0.2, 0.2];
weight_color = [0.4, 0.4, 0.4];

// Main body - glass tube with bulb at bottom
module salinometer_body() {
    color(glass_color) {
        // Main cylindrical tube
        cylinder(h=body_length, d=body_diameter, center=false);

        // Weight bulb at bottom
        translate([0, 0, -bulb_diameter/2])
            sphere(d=bulb_diameter);

        // Thin stem at top for reading
        translate([0, 0, body_length])
            cylinder(h=stem_length, d=body_diameter-4, center=false);
    }
}

// Internal weight for proper buoyancy
module internal_weight() {
    color(weight_color) {
        // Lead shot chamber in bulb
        translate([0, 0, -bulb_diameter/3])
            sphere(d=bulb_diameter-2);

        // Additional weight cylinder
        translate([0, 0, 0])
            cylinder(h=10, d=body_diameter-2, center=false);
    }
}

// Scale markings for salinity reading
module scale_markings() {
    color(scale_color) {
        // Main scale line
        translate([body_diameter/2 + 1, 0, body_length/2])
            cube([0.5, 0.5, scale_length], center=true);

        // Individual graduations
        for (i = [0:4]) {
            translate([body_diameter/2 + 1, 0, body_length/2 + i*6])
                cube([2, 0.5, 0.5], center=true);

            translate([body_diameter/2 + 1, 0, body_length/2 + i*6 + 3])
                cube([1.5, 0.3, 0.3], center=true);
        }

        // Salinity labels (0-25%)
        for (i = [0:4]) {
            translate([body_diameter/2 + 3.5, 0, body_length/2 + i*6])
                rotate([0, 90, 0])
                    cylinder(h=0.5, d=0.5, center=true);
        }
    }
}

// Reading line indicator
module reading_line() {
    color([1, 0, 0, 0.8]) {
        translate([0, 0, body_length/2 + 12])
            rotate([90, 0, 0])
                cylinder(h=body_diameter+2, d=0.5, center=true);
    }
}

// Support stand for storage
module storage_stand() {
    color([0.3, 0.3, 0.3]) {
        // Base
        translate([0, 0, -bulb_diameter/2 - 5])
            cylinder(h=5, d=25, center=false);

        // Support arms
        for (a = [0:120:240]) {
            rotate([0, 0, a])
                translate([10, 0, -2.5])
                    cube([20, 3, 5], center=true);
        }

        // Center hole
        translate([0, 0, -bulb_diameter/2 - 5])
            cylinder(h=10, d=body_diameter+4, center=false);
    }
}

// Assembly
// Main salinometer
union() {
    salinometer_body();

    // Internal weights (not visible but affects buoyancy)
    %internal_weight();

    // Scale markings on side
    scale_markings();

    // Reading line
    reading_line();
}

// Optional storage stand
translate([40, 0, 0])
    storage_stand();

// Polarimeter Tool - Realistic Style, Medium Complexity
// Features: Sample chamber, rotating analyzer, graduated scale, light source

$fn = 50;
wall_thickness = 2;
body_color = [0.8, 0.8, 0.8];
analyzer_color = [0.2, 0.2, 0.8];
scale_color = [0.1, 0.1, 0.1];
light_color = [1, 1, 0.5];

module polarimeter() {
    // Base cylinder for the main body
    color(body_color) {
        difference() {
            cylinder(h = 80, r = 25, center = true);
            cylinder(h = 82, r = 23, center = true);
        }
    }

    // Light source at bottom
    translate([0, 0, -40]) {
        color(light_color) {
            sphere(r = 8);
            translate([0, 0, 2]) {
                cylinder(h = 5, r = 6);
            }
        }
    }

    // Sample tube holder
    translate([0, 0, 0]) {
        color(body_color) {
            difference() {
                cylinder(h = 40, r = 15);
                cylinder(h = 42, r = 13);
            }
        }

        // Sample tube (glass)
        color([0.9, 0.9, 1, 0.5]) {
            cylinder(h = 38, r = 12);
        }
    }

    // Rotating analyzer assembly
    module analyzer() {
        rotate([0, 0, $t * 360]) {
            // Analyzer disk
            color(analyzer_color) {
                difference() {
                    cylinder(h = 3, r = 20);
                    cylinder(h = 4, r = 18);
                }
            }

            // Polarizing filter lines
            for (i = [0:5]) {
                rotate([0, 0, i * 30]) {
                    translate([-20, -0.5, 0]) {
                        cube([40, 1, 3]);
                    }
                }
            }

            // Handle for rotation
            translate([15, 0, 1.5]) {
                rotate([90, 0, 0]) {
                    cylinder(h = 8, r = 2);
                }
            }
        }
    }

    translate([0, 0, 30]) {
        analyzer();
    }

    // Graduated scale
    translate([0, 0, 31.5]) {
        color(scale_color) {
            difference() {
                cylinder(h = 1, r = 22);
                for (i = [0:359]) {
                    if (i % 10 == 0) {
                        rotate([0, 0, i]) {
                            translate([20, -0.5, 0]) {
                                cube([2, 1, 1]);
                            }
                        }
                    } else if (i % 5 == 0) {
                        rotate([0, 0, i]) {
                            translate([20.5, -0.25, 0]) {
                                cube([1, 0.5, 1]);
                            }
                        }
                    }
                }
            }
        }
    }

    // Eyepiece
    translate([0, 0, 40]) {
        color(body_color) {
            difference() {
                cylinder(h = 15, r = 12);
                cylinder(h = 16, r = 10);
            }
        }

        // Eyepiece lens
        color([0.8, 0.8, 1, 0.7]) {
            translate([0, 0, 7.5]) {
                sphere(r = 10);
            }
        }
    }

    // Support feet
    for (i = [0:2]) {
        rotate([0, 0, i * 120]) {
            translate([0, 20, -40]) {
                color(body_color) {
                    cylinder(h = 5, r = 3);
                }
            }
        }
    }
}

// Render the polarimeter
polarimeter();

// Brixmeter (Refractometer) - Medium Complexity
// Realistic style with printable features

// Color definitions
$fn=50;

module brixmeter_body() {
    // Main body cylinder - slightly tapered
    difference() {
        // Outer shell
        cylinder(h=35, r1=12, r2=10);

        // Internal cavity for optics
        translate([0,0,5])
            cylinder(h=25, r1=9, r2=7);
    }
}

module eyepiece() {
    // Eyepiece with rubber grip
    color("black")
    difference() {
        // Main eyepiece tube
        translate([0,0,35])
            cylinder(h=15, r=6);

        // Viewing hole
        translate([0,0,35])
            cylinder(h=15, r=3);
    }

    // Rubber grip rings
    for(i=[0:1]) {
        color("darkgray")
        translate([0,0,37+i*6])
            rotate_extrude()
                translate([6,0,0])
                    circle(r=0.5);
    }
}

module prism_compartment() {
    // Sample prism housing at bottom
    color("gray")
    difference() {
        // Rectangular housing
        translate([0,0,-5])
            cube([18,18,5], center=true);

        // Prism cavity
        translate([0,0,-2.5])
            cube([14,14,6], center=true);

        // Sample window
        translate([0,0,-5.1])
            cube([10,10,1], center=true);
    }

    // Hinged cover
    color("silver")
    translate([9,0,-5])
        rotate([0,0,90])
            cube([12,1.5,8]);
}

module focus_knob() {
    // Focusing adjustment ring
    color("darkgray")
    rotate([90,0,0])
        translate([0,-12,15])
            difference() {
                // Main ring
                cylinder(h=5, r=4);

                // Knurling cuts
                for(i=[0:11]) {
                    rotate([0,0,i*30])
                        translate([-0.5,-4.5,0])
                            cube([1,5,5]);
                }
            }
}

module calibration_screw() {
    // Calibration adjustment screw
    color("silver")
    translate([10,0,25])
        rotate([90,0,0])
            cylinder(h=3, r=2);

    // Screw head slot
    color("black")
    translate([10,0,25])
        rotate([0,90,0])
            cube([3,0.5,1.5], center=true);
}

module measurement_scale() {
    // Brix scale markings
    color("black")
    for(i=[0:20]) {
        translate([0,10.1,30-i*1.2])
            rotate([90,0,0])
                linear_extrude(0.1)
                    text(str(i), size=1, halign="center");
    }
}

module grip_texture() {
    // Anti-slip texture on body
    color("darkgray")
    for(i=[0:7]) {
        rotate([0,0,i*45])
            translate([11,0,15])
                rotate([90,0,0])
                    cylinder(h=20, r=0.5);
    }
}

// Assembly
union() {
    // Main body in silver
    color("silver") brixmeter_body();

    // All components
    eyepiece();
    prism_compartment();
    focus_knob();
    calibration_screw();
    measurement_scale();
    grip_texture();
}

// Realistic Pycnometer (density bottle) 
// Medium complexity, 3D-printable, ~70 mm tall

// Overall scale factor
scale_factor = 1;

// Colors
glass_color = [0.8, 0.9, 1.0, 0.4];
cap_color   = [0.2, 0.2, 0.2, 1.0];
metal_color = [0.5, 0.5, 0.5, 1.0];
label_color = [0.0, 0.0, 0.0, 0.6];

// Main dimensions
body_h  = 55 * scale_factor;
body_r  = 15 * scale_factor;
neck_h  = 12 * scale_factor;
neck_r  = 6 * scale_factor;
cap_h   = 15 * scale_factor;
cap_r   = 8 * scale_factor;

// Wall thickness for 3D printing
wall = 2 * scale_factor;

module body() {
    // Hollow glass bulb
    difference() {
        color(glass_color) cylinder(h = body_h, r = body_r, center = true);
        translate([0, 0, -wall/2])
            cylinder(h = body_h + wall, r = body_r - wall, center = true);
    }
}

module neck() {
    // Cylindrical neck with small opening
    difference() {
        color(glass_color) cylinder(h = neck_h, r = neck_r, center = true);
        translate([0, 0, -wall/2])
            cylinder(h = neck_h + wall, r = neck_r - wall, center = true);
    }
}

module cap() {
    // Screw cap with knurled grip
    difference() {
        color(cap_color) cylinder(h = cap_h, r = cap_r, center = true);
        translate([0, 0, -wall/2])
            cylinder(h = cap_h + wall, r = cap_r - wall, center = true);
    }

    // Knurled texture around cap
    for(i = [0:30:359]) {
        rotate([0, 0, i])
            translate([cap_r - 1.5, 0, 0])
                difference() {
                    color(cap_color) cube([1.5, 1, cap_h - 2], center = true);
                    translate([0, 0, -1])
                        cube([1.5, 1.2, cap_h], center = true);
                }
    }
}

module stopper() {
    // Glass stopper with capillary tube
    difference() {
        color(glass_color) cylinder(h = neck_h - 2, r = neck_r - 0.5, center = true);
        // Capillary bore
        cylinder(h = neck_h - 1, r = 1.5, center = true);
    }
}

module labeling() {
    // Raised volume marking
    translate([0, body_r + 0.5, 0])
        rotate([90, 0, 0])
            color(label_color) linear_extrude(height = 0.5)
                text("25 mL", size = 4, halign = "center");
}

// Assembly
translate([0, 0, body_h/2 + neck_h/2])
    union() {
        body();
        translate([0, 0, body_h/2 + neck_h/2])
            neck();
        translate([0, 0, body_h/2 + neck_h + cap_h/2])
            cap();
        translate([0, 0, body_h/2 + neck_h/2])
            stopper();
        labeling();
    }

// Areometer (Hydrometer) - Realistic Style, Medium Complexity
// 3D printable hydrometer for measuring liquid density

// Parameters
$fn = 36;  // Smoothness
wall = 1.5;  // Wall thickness for 3D printing
scale_min = 1.0;
scale_max = 1.2;

// Colors
color_body = "White";
color_scale = "Black";
color_bulb = "Crimson";

// Main module
module areometer() {
    // Base bulb for buoyancy
    color(color_bulb) translate([0, 0, 8]) 
        difference() {
            sphere(d = 22);
            cylinder(h = 12, d1 = 22, d2 = 18);  // Flatten bottom
        }

    // Main stem tube
    color(color_body) translate([0, 0, 22])
        difference() {
            cylinder(h = 60, d = 8);
            translate([0, 0, -1]) cylinder(h = 62, d = 8 - 2*wall);
        }

    // Top weight chamber
    color(color_body) translate([0, 0, 82])
        difference() {
            sphere(d = 12);
            sphere(d = 12 - 2*wall);
        }

    // Scale markings module
    module scale_mark(pos, length, width, height) {
        translate([0, 0, pos])
            cube([width, length, height], center = true);
    }

    // Generate scale markings
    color(color_scale) {
        for (i = [0:10]) {
            pos = 25 + i * 5;
            // Long marks every 2nd position
            mark_length = (i % 2 == 0) ? 3 : 1.5;
            mark_width = (i % 2 == 0) ? 0.8 : 0.5;
            scale_mark(pos, mark_length, mark_width, 0.2);

            // Numbers on long marks
            if (i % 2 == 0) {
                translate([4, 0, pos - 1])
                    rotate([0, 90, 0])
                    linear_extrude(0.5)
                    text(str(scale_min + i * 0.02), size = 2, halign = "left");
            }
        }
    }

    // Reference line
    color("Blue") translate([0, -4.5, 0])
        cube([0.5, 9, 100], center = true);
}

// Add support ring for 3D printing
color("Gray") translate([0, 0, -1])
    difference() {
        cylinder(h = 2, d = 30);
        cylinder(h = 2.5, d = 20);
    }

// Render the complete areometer
areometer();

// Spirometer - Medium Complexity Realistic Style
// Dimensions in mm, designed for 3D printing

// Color definitions
color_body = [0.9, 0.9, 0.9];  // Light gray plastic
color_tube = [0.8, 0.8, 1];    // Light blue translucent
color_grip = [0.2, 0.2, 0.2];  // Black rubber
color_markings = [0.1, 0.1, 0.1]; // Dark gray

// Main body parameters
body_length = 80;
body_diameter = 35;
tube_diameter = 22;
mouthpiece_length = 25;
mouthpiece_diameter = 18;

// Scale markings
scale_positions = [10, 20, 30, 40, 50, 60, 70];
scale_width = 2;
scale_height = 1;

module main_body() {
    color(color_body) {
        // Central cylindrical body
        difference() {
            cylinder(h=body_length, d=body_diameter, center=true);
            // Hollow center
            cylinder(h=body_length+2, d=tube_diameter, center=true);
        }

        // End caps with rounded edges
        translate([0, 0, body_length/2]) {
            cylinder(h=3, d1=body_diameter, d2=body_diameter-4);
        }
        translate([0, 0, -body_length/2]) {
            cylinder(h=3, d1=body_diameter-4, d2=body_diameter);
        }
    }
}

module mouthpiece() {
    color(color_tube) {
        // Mouthpiece tube
        translate([0, 0, body_length/2 + mouthpiece_length/2]) {
            cylinder(h=mouthpiece_length, d1=tube_diameter, d2=mouthpiece_diameter);
        }

        // Flared end for comfort
        translate([0, 0, body_length/2 + mouthpiece_length]) {
            cylinder(h=5, d1=mouthpiece_diameter, d2=mouthpiece_diameter+4);
        }
    }
}

module grip_zones() {
    color(color_grip) {
        // Left grip zone
        for(i = [-1, 1]) {
            rotate([0, 90, 0]) {
                translate([i * (body_diameter/2 + 3), 0, 0]) {
                    difference() {
                        cylinder(h=30, d=8, center=true);
                        cylinder(h=32, d=6, center=true);
                    }
                }
            }
        }

        // Texture rings on grips
        for(i = [-1, 1]) {
            rotate([0, 90, 0]) {
                translate([i * (body_diameter/2 + 3), 0, -10]) {
                    for(z = [-10:5:10]) {
                        translate([0, 0, z]) {
                            cylinder(h=1, d=9);
                        }
                    }
                }
            }
        }
    }
}

module measurement_scale() {
    color(color_markings) {
        // Main scale markings
        for(pos = scale_positions) {
            translate([tube_diameter/2 + 1, 0, -body_length/2 + pos]) {
                cube([scale_width, body_diameter/2, scale_height], center=true);
            }

            // Number indicators (simplified as blocks)
            if(pos % 20 == 0) {
                translate([tube_diameter/2 + 4, 0, -body_length/2 + pos]) {
                    cube([4, 2, 8], center=true);
                }
            }
        }

        // Maximum capacity indicator
        translate([tube_diameter/2 + 1, 0, body_length/2 - 5]) {
            color([0.8, 0.2, 0.2])  // Red warning zone
            cube([scale_width+2, body_diameter/2-2, 3], center=true);
        }
    }
}

module air_outlet() {
    color(color_tube) {
        // Outlet tube at bottom
        translate([0, 0, -body_length/2 - 10]) {
            cylinder(h=15, d=tube_diameter-4);

            // Connection flange
            translate([0, 0, -2]) {
                cylinder(h=3, d=tube_diameter+2);
            }
        }
    }
}

// Assembly
$fn = 32;  // Smooth curves

union() {
    main_body();
    mouthpiece();
    grip_zones();
    measurement_scale();
    air_outlet();
}

// Respirometer Tool - Medium Complexity
// Includes main chamber, graduated markings, connecting tubes, and base stand

// Color definitions
color_body = [0.9, 0.9, 0.9];  // White plastic
color_markings = [0.2, 0.2, 0.2];  // Black markings
color_tubes = [0.1, 0.9, 0.2];  // Green tubes
color_base = [0.4, 0.4, 0.4];  // Gray base

// Overall dimensions
base_length = 80;
base_width = 40;
base_height = 10;
chamber_diameter = 20;
chamber_height = 60;
tube_diameter = 4;
tube_length = 25;

// Main chamber module
module chamber() {
    color(color_body) {
        // Main cylindrical chamber
        cylinder(h=chamber_height, d=chamber_diameter, $fn=32);

        // Chamber top cap
        translate([0, 0, chamber_height])
            cylinder(h=3, d=chamber_diameter+2, $fn=32);

        // Chamber bottom
        translate([0, 0, -1])
            cylinder(h=2, d=chamber_diameter+2, $fn=32);
    }

    // Graduated markings
    color(color_markings) {
        for(i=[0:5:50]) {
            translate([chamber_diameter/2 + 1, 0, i])
                cube([1, 0.5, 0.5], center=true);
        }
    }
}

// Tube connection module
module tube_connection(pos, angle) {
    translate(pos) {
        rotate([90, angle, 0]) {
            color(color_tubes) {
                // Main tube
                cylinder(h=tube_length, d=tube_diameter, $fn=16);

                // Tube connector
                translate([0, 0, tube_length])
                    cylinder(h=5, d=tube_diameter+2, $fn=16);

                // Tube base
                translate([0, 0, -1])
                    cylinder(h=2, d=tube_diameter+2, $fn=16);
            }
        }
    }
}

// Base stand module
module base() {
    color(color_base) {
        // Main base
        translate([0, 0, base_height/2])
            cube([base_length, base_width, base_height], center=true);

        // Chamber support
        translate([0, 0, base_height + chamber_height/2])
            difference() {
                cube([chamber_diameter + 6, chamber_diameter + 6, chamber_height], center=true);
                cube([chamber_diameter + 2, chamber_diameter + 2, chamber_height + 2], center=true);
            }

        // Base supports
        for(i=[-1:1]) {
            translate([i * 25, 0, base_height + 5])
                cube([5, base_width - 10, 10], center=true);
        }
    }
}

// Assembly
union() {
    // Base and chamber
    base();

    translate([0, 0, base_height + 5]) {
        chamber();

        // Tube connections
        tube_connection([0, chamber_diameter/2 + 5, chamber_height - 10], 0);
        tube_connection([chamber_diameter/2 + 5, 0, chamber_height/2], 90);
        tube_connection([-chamber_diameter/2 - 5, 0, chamber_height/2], -90);
    }

    // Additional support structures
    color(color_body) {
        translate([0, 0, base_height + 2])
            cylinder(h=3, d=chamber_diameter + 4, $fn=32);
    }
}

