module geiger_body() {
    difference() {
        // Main body
        color("darkolivegreen")
        rounded_box(body_length, body_width, body_height, 3);

        // Tube cavity
        translate([body_length/2 - tube_length/2 - 5, 0, body_height/2 - tube_diameter/2])
            rotate([0, 90, 0])
            cylinder(d=tube_diameter + 1, h=tube_length + 2, center=true);

        // Display window
        translate([-body_length/2 + 10, 0, body_height/2 - 1])
            cube([screen_depth + 1, screen_width + 2, screen_height + 2], center=true);

