// Solid base with ergonomic grip
module base() {
    difference() {
        // Main base cylinder
        cylinder(d = base_d, h = base_h);

        // Grip grooves on sides
        for (i = [0:3]) {
            rotate([0, 0, i * 90])
                translate([base_d/2 - 3, -1.5, 1])
                    cube([5, 3, base_h - 2], center = false);
        }

        // Center hole for tube
        translate([0, 0, -1])
            cylinder(d = tube_d + 2, h = base_h + 2);
    }

    // Stabilizing ring on bottom
    translate([0, 0, -1])
        cylinder(d1 = base_d, d2 = base_d + 5, h = 3);
