// Glass tube with expansion bulb
module glass_tube() {
    // Vertical tube
    translate([0, 0, base_h])
        cylinder(d = tube_d, h = tube_h);

    // Expansion bulb at bottom
    translate([0, 0, base_h - bulb_h/2])
        sphere(d = bulb_d);

