    // In bulb
    translate([0, 0, base_h - bulb_h/2])
        sphere(d = bulb_d - 1);

    // In tube (height varies with temperature)
    translate([0, 0, base_h])
        cylinder(d = tube_d - 1, h = tube_h * 0.6);
