color_glass = [0.8, 0.9, 1.0, 0.5];  // Transparent blue
color_markings = [0, 0, 0];     // Black

// Main assembly
module hygroscope() {
    // Main instrument body
    color(color_body)
    translate([0, 0, 0])
        instrument_body();

    // Dial assembly
    color(color_dial)
    translate([0, body_height/2 - dial_thickness - 2, body_depth/2 + 1])
        rotate([90, 0, 0])
            dial_assembly();

    // Hair tension mechanism
    color(color_hair)
    translate([0, body_height/2 - 10, body_depth/2 - 5])
        hair_mechanism();

    // Handle
    color(color_body)
    translate([0, -body_height/2 - handle_length/2, 0])
        rotate([90, 0, 0])
            handle();

    // Protective glass cover
    color(color_glass)
    translate([0, body_height/2 - dial_thickness - 2, body_depth/2 + 3])
        rotate([90, 0, 0])
            glass_cover();
}

// Instrument body with mounting features
module instrument_body() {
    difference() {
        // Main body
        cube([body_width, body_height, body_depth], center=true);

        // Rounded edges
        translate([0, 0, body_depth/2])
            cylinder(h=body_depth+2, r=body_width/2-5, $fn=20);

        // Mounting holes
        for(x = [-body_width/3, body_width/3]) {
            for(y = [-body_height/3, body_height/3]) {
                translate([x, y, 0])
                    cylinder(h=body_depth+2, r=2, $fn=12);
            }
        }

        // Ventilation slots
        for(i = [-2:2]) {
            translate([i*6, body_height/2 - 10, 0])
                cube([2, 20, body_depth+1], center=true);
        }
    }
}

// Dial assembly with markings
module dial_assembly() {
    difference() {
        // Main dial
        cylinder(h=dial_thickness, r=dial_radius, $fn=60);

        // Center hole
        cylinder(h=dial_thickness+1, r=3, $fn=20);
    }

    // Dial markings
    color(color_markings)
    for(i = [0:359:30]) {
        rotate([0, 0, i])
            translate([dial_radius-5, 0, dial_thickness/2])
                cube([5, 1, 0.5], center=true);
    }

    // Humidity scale labels
    for(i = [0:3]) {
        angle = i * 90;
        value = i * 25;
        rotate([0, 0, angle - 90])
            translate([dial_radius-10, 0, dial_thickness/2 + 0.1])
                rotate([0, 0, -angle + 90])
                    scale([0.1, 0.1, 0.1])
                        linear_extrude(height=1)
                            text(str(value), size=5, halign="center");
    }

    // Indicator needle
    color(color_markings)
    translate([0, 0, dial_thickness/2 + 0.2])
        rotate([0, 0, 45])  // Set to 45% humidity
        union() {
            cube([dial_radius-8, 1.5, 0.5], center=true);
            translate([dial_radius-8, 0, 0])
                sphere(d=1.5);
        }
}

// Hair tension mechanism
