    // Hair mounting posts
    for(x = [-hair_length/2, hair_length/2]) {
        translate([x, 0, 0])
            cylinder(h=10, r=2, $fn=12);
    }

    // Human hair strand (simplified as cylinder)
    translate([0, 0, 5])
        rotate([90, 0, 0])
            cylinder(h=hair_length, r=hair_diameter, $fn=8);

    // Tension adjustment screw
    translate([hair_length/2 + 5, 0, 5])
        union() {
            cylinder(h=8, r=3, $fn=12);
            translate([0, 0, 8])
                cylinder(h=3, r=5, $fn=6);
