color_buttons = "Red";
color_display = "Black";
color_glass = "White";
color_strap = "DarkGray";

// Case dimensions
case_d = 80;
case_h = 15;
bezel_w = 5;

// Button dimensions
button_d = 8;
button_h = 20;
button_gap = 5;

// Display dimensions
display_d = 60;
display_h = 1;
display_offset = 2;

// Crown dimensions
crown_d = 12;
crown_h = 8;

// Strap dimensions
strap_w = 22;
strap_h = 3;
strap_l = 100;

// Main case
module case() {
    color(color_case)
    difference() {
        // Outer case with beveled edges
        cylinder(d = case_d, h = case_h);
        translate([0, 0, -0.1])
            cylinder(d = case_d - 2*bezel_w, h = case_h + 0.2);

        // Button holes
        for (i = [-1, 1]) {
            translate([i * (case_d/2 + button_gap), 0, case_h/2])
                rotate([0, 90, 0])
                    cylinder(d = button_d, h = button_gap + 2);
        }

        // Crown hole
        translate([0, case_d/2 + crown_h/2, case_h/2 - crown_d/2])
            rotate([90, 0, 0])
                cylinder(d = crown_d, h = crown_h + 2);
    }
}

// Bezel with markings
module bezel() {
    color(color_case)
    difference() {
        cylinder(d = case_d, h = bezel_w);
        translate([0, 0, -0.1])
            cylinder(d = case_d - 2*bezel_w, h = bezel_w + 0.2);
    }

    // Tachymeter markings
    for (i = [0:5:355]) {
        translate([0, 0, bezel_w - 0.5])
            rotate([0, 0, i])
                translate([(case_d - bezel_w)/2 - 2, 0, 0])
                    cube([4, 1, 0.5], center = true);
    }
}

// Display assembly
module display() {
    // Glass cover
    color(color_glass, 0.3)
    translate([0, 0, case_h - display_offset])
        cylinder(d = display_d, h = 1);

    // LCD display
    color(color_display)
    translate([0, 0, case_h - display_offset - 1])
        cylinder(d = display_d - 2, h = display_h);

    // Sub-dials
    for (pos = [[-20, 15], [20, 15], [0, -20]]) {
        translate([pos[0], pos[1], case_h - display_offset - 0.5])
            cylinder(d = 12, h = 0.5);
        translate([pos[0], pos[1], case_h - display_offset])
            cylinder(d = 1, h = 2);
    }
}

// Push buttons
module buttons() {
    for (i = [-1, 1]) {
        color(color_buttons)
        translate([i * (case_d/2 + button_gap/2), 0, case_h/2])
            rotate([0, 90, 0])
                cylinder(d1 = button_d, d2 = button_d*0.8, h = button_h);
    }
}

// Crown
module crown() {
    color(color_case)
    translate([0, case_d/2 + crown_h/2, case_h/2 - crown_d/2])
        rotate([90, 0, 0])
            cylinder(d = crown_d, h = crown_h);

    // Crown grip pattern
    for (i = [0:30:330]) {
        translate([0, case_d/2 + crown_h/2, case_h/2 - crown_d/2])
            rotate([90, i, 0])
                translate([crown_d/2 - 0.5, 0, 0])
                    cube([1, crown_d*0.8, 0.5], center = true);
    }
}

// Watch strap
module strap() {
    // Top strap
    color(color_strap)
    translate([0, case_d/2 + strap_l/2, -case_h/2])
        cube([strap_w, strap_l, strap_h], center = true);

    // Bottom strap
    translate([0, -case_d/2 - strap_l/2, -case_h/2])
        cube([strap_w, strap_l, strap_h], center = true);

    // Strap loops
    for (i = [0:20:60]) {
        translate([0, case_d/2 + strap_l - i, -case_h/2])
            difference() {
                cube([strap_w + 2, 8, strap_h + 2], center = true);
                cube([strap_w - 2, 12, strap_h], center = true);
            }
    }
}

// Assembly
case();
bezel();
display();
buttons();
crown();
strap();

// Planigraph - Medium Complexity Realistic Style
// A drawing tool for creating parallel lines and measurements

$fn=50;

// Main colors
color_plastic = [0.15,0.15,0.15];  // Dark grey body
color_metal = [0.7,0.7,0.7];       // Light grey metal parts
color_rubber = [0.1,0.1,0.1];      // Black rubber grips
color_highlight = [0.9,0.1,0.1];   // Red accent

// Overall dimensions
body_length = 80;
body_width = 15;
body_height = 8;

module body() {
    color(color_plastic)
    difference() {
        // Main body
        cube([body_length, body_width, body_height], center=true);

        // Center slot for ruler
        translate([0, 0, body_height/2 - 0.5])
        cube([body_length - 10, 6, 1.5], center=true);
    }
}

module grip_texture() {
    // Rubber grip pattern
    for(i=[-30:5:30]) {
        translate([i, body_width/2 + 2, 0])
        color(color_rubber)
        cube([3, 4, body_height - 1]);
    }
}

module ruler_slot() {
    // Metal ruler channel
    translate([0, 0, body_height/2 + 1])
    color(color_metal)
    cube([body_length - 8, 2, 1], center=true);
}

module ruler() {
    // Sliding ruler
    translate([0, 0, body_height/2 + 1.5])
    color(color_metal)
    difference() {
        cube([body_length - 12, 1.5, 0.5], center=true);

        // Measurement markings
        for(i=[-35:5:35]) {
            translate([i, 0, 0.25])
            cube([0.5, 1.5, 0.25]);
        }

        // Every 10mm longer mark
        for(i=[-30:10:30]) {
            translate([i, 0, 0.25])
            cube([1, 1.5, 0.25]);
        }
    }
}

module thumb_wheel() {
    // Adjustment wheel on side
    translate([body_length/2 - 5, body_width/2 + 3, 0])
    rotate([90,0,0])
    color(color_metal)
    difference() {
        cylinder(h=3, r=6, center=true);

        // Knurling pattern
        for(i=[0:15:359]) {
            rotate([0,0,i])
            translate([5.5, 0, 0])
            cube([1, 0.5, 3.2], center=true);
        }

        // Center hole
        cylinder(h=4, r=1.5, center=true);
    }
}

module guide_pins() {
    // Two metal guide pins at ends
    for(pos=[-35, 35]) {
        translate([pos, 0, -body_height/2 - 2])
        color(color_metal)
        cylinder(h=4, r=2);
    }
}

module pencil_holder() {
    // Pencil clamp mechanism
    translate([-body_length/2 - 10, 0, 0])
    color(color_plastic)
    union() {
        // Clamp body
        cube([10, 8, body_height], center=true);

        // Pencil hole
        translate([0, 0, 0])
        rotate([90,0,0])
        color(color_highlight)
        cylinder(h=12, r=3.5);

        // Clamp screw
        translate([5, 0, body_height/2 + 2])
        color(color_metal)
        cylinder(h=6, r=2);
    }

    // Actual pencil hole (negative)
    translate([-body_length/2 - 10, 0, 0])
    rotate([90,0,0])
    cylinder(h=20, r=4);
}

module scale_markings() {
    // Body scale markings
    for(i=[-35:5:35]) {
        translate([i, body_width/2 + 1, body_height/2 - 0.5])
        color([1,1,1])
        cube([0.5, 0.5, 0.5]);
    }
}

// Assembly
union() {
    body();
    grip_texture();
    ruler_slot();
    ruler();
    thumb_wheel();
    guide_pins();
    pencil_holder();
    scale_markings();
}

// Goniograph Tool - Medium Complexity Realistic Style
// A tool for measuring crystal angles and orientations

// Color definitions
color_handle = "DarkGoldenrod";
color_metal = "Silver";
color_scale = "Black";
color_glass = "LightBlue";

// Main parameters
base_diameter = 60;
base_height = 8;
handle_length = 80;
handle_diameter = 12;
arm_length = 45;
arm_thickness = 4;
stage_diameter = 35;
stage_height = 3;
lens_diameter = 18;
lens_height = 8;

// Base module with degree markings
module base() {
    color(color_metal)
    difference() {
        cylinder(h=base_height, d=base_diameter, $fn=60);

        // Central hole for handle
        cylinder(h=base_height+1, d=handle_diameter+2, $fn=30);

        // Degree markings (every 10 degrees)
        for(i=[0:35]) {
            rotate([0,0,i*10])
            translate([base_diameter/2-4, -0.5, -0.1])
            cube([4,1,base_height+0.2]);
        }

        // Main degree numbers
        for(i=[0:11]) {
            rotate([0,0,i*30])
            translate([base_diameter/2-7, -1.5, -0.1])
            cube([2,3,base_height+0.2]);
        }
    }

    // Number indicators
    color(color_scale)
    for(i=[0:11]) {
        rotate([0,0,i*30])
        translate([base_diameter/2-10, -2, base_height/2+1])
        linear_extrude(height=2)
        text(str(i*30), size=3, halign="center");
    }
}

// Rotating handle with grip texture
module handle() {
    color(color_handle)
    difference() {
        cylinder(h=handle_length, d=handle_diameter, $fn=30);

        // Grip rings
        for(i=[handle_diameter:handle_length-handle_diameter]) {
            translate([0,0,i])
            cylinder(h=1.5, d=handle_diameter-2, $fn=30);
        }
    }

    // Top collar
    color(color_metal)
    translate([0,0,handle_length])
    cylinder(h=6, d=handle_diameter+4, $fn=30);
}

// Measuring arm with vernier scale
module measuring_arm() {
    color(color_metal)
    union() {
        // Main arm
        translate([handle_diameter/2+2, -arm_thickness/2, 0])
        cube([arm_length, arm_thickness, 6]);

        // Vernier scale attachment
        translate([arm_length-5, -arm_thickness/2-3, 3])
        cube([10, arm_thickness+6, 2]);

        // Vernier scale markings
        color(color_scale)
        for(i=[0:9]) {
            translate([arm_length-4.5+i, -arm_thickness/2-2, 5.1])
            cube([0.5, arm_thickness+4, 0.2]);
        }
    }

    // Pointer tip
    translate([arm_length, -1, 0])
    cylinder(h=6, d1=2, d2=0, $fn=16);
}

// Sample stage with crystal holder
module sample_stage() {
    color(color_metal)
    difference() {
        // Main stage
        cylinder(h=stage_height, d=stage_diameter, $fn=60);

        // Center hole
        cylinder(h=stage_height+1, d=4, $fn=20);

        // Radial grooves for crystal alignment
        for(i=[0:7]) {
            rotate([0,0,i*45])
            translate([2, -0.5, -0.1])
            cube([stage_diameter/2-2, 1, stage_height+0.2]);
        }
    }

    // Crystal clamp
    color(color_metal)
    translate([0, stage_diameter/2-3, stage_height])
    rotate([90,0,0])
    cylinder(h=8, d1=4, d2=2, $fn=16);
}

// Magnifying lens assembly
module lens_assembly() {
    color(color_metal)
    difference() {
        // Lens housing
        cylinder(h=lens_height, d=lens_diameter+4, $fn=30);

        // Lens cavity
        translate([0,0,1])
        cylinder(h=lens_height-2, d=lens_diameter, $fn=30);

        // Light path
        translate([0,0,-0.1])
        cylinder(h=lens_height+0.2, d=lens_diameter/2, $fn=20);
    }

    // Actual lens
    color(color_glass, 0.6)
    translate([0,0,lens_height/2])
    sphere(d=lens_diameter, $fn=30);
}

// Assembly
// Base
base();

// Handle (rotating part)
translate([0,0,base_height])
handle();

// Measuring arm (attached to handle collar)
translate([0,0,base_height+handle_length+6])
rotate([90,0,0])
measuring_arm();

// Sample stage
translate([0,0,base_height+handle_length+12])
sample_stage();

// Lens assembly (adjustable position)
translate([arm_length-15, 0, base_height+handle_length+15])
rotate([90,0,0])
lens_assembly();

// Dynamometer Tool - Realistic Style, Medium Complexity
// 3D printable handheld force gauge with digital display

// Main parameters
$fn = 32;
body_length = 80;
body_width = 40;
body_height = 20;
handle_length = 60;
handle_diameter = 12;
hook_thickness = 3;
hook_radius = 8;

// Main body
module main_body() {
    difference() {
        // Outer shell
        color("silver") {
            translate([0, 0, body_height/2])
                cube([body_length, body_width, body_height], center=true);
        }

        // Screen cavity
        color("white") {
            translate([-body_length/2 + 10, 0, body_height/2 + 0.5])
                cube([30, 25, 5], center=true);
        }

        // Grip indentations
        for(i = [0:3]) {
            translate([body_length/2 - 15 - i*10, body_width/2, body_height/2])
                cube([8, 2, 12], center=true);
        }
    }
}

// Handle assembly
module handle() {
    color("darkblue") {
        translate([body_length/2 + handle_length/2 - 10, 0, -body_height/2 + handle_diameter/2])
            cylinder(h=handle_length, d=handle_diameter, center=true);
    }

    // Handle grip rings
    for(i = [0:4]) {
        color("black") {
            translate([body_length/2 + 5 + i*10, 0, -body_height/2 + handle_diameter/2])
                cylinder(h=2, d=handle_diameter + 2, center=true);
        }
    }
}

// Digital display
module display() {
    color("black") {
        translate([-body_length/2 + 12, 0, body_height/2 + 1])
            cube([26, 21, 0.5], center=true);
    }

    // Display digits
    color("green") {
        for(x = [-10:10:10]) {
            translate([x, -5, body_height/2 + 2])
                cube([1, 8, 0.2], center=true);
        }
    }
}

// Hook assembly
module measurement_hook() {
    // Hook base
    color("silver") {
        translate([-body_length/2 - 5, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=10, d=8, center=true);
    }

    // Hook curve
    color("silver") {
        translate([-body_length/2 - 12, 0, 0])
            rotate([0, 90, 0])
                difference() {
                    cylinder(h=hook_thickness, d=hook_radius*2, center=true);
                    translate([0, -hook_radius, 0])
                        cube([hook_thickness*2, hook_radius*2, hook_thickness*2], center=true);
                }
    }

    // Hook tip
    color("silver") {
        translate([-body_length/2 - 12, hook_radius, 0])
            rotate([0, 90, 0])
                cylinder(h=hook_thickness, d=2, center=true);
    }
}

// Buttons
module control_buttons() {
    for(i = [0:1]) {
        color("red") {
            translate([-body_length/2 + 25, body_width/2 - 5 - i*8, body_height/2 + 1])
                cylinder(h=2, d=6, center=false);
        }
    }
    color("green") {
        translate([-body_length/2 + 35, body_width/2 - 5, body_height/2 + 1])
            cylinder(h=2, d=6, center=false);
    }
}

// Assembly
main_body();
handle();
display();
measurement_hook();
control_buttons();

// Dynamograph - Medium complexity realistic style
// A dynamograph is a device for measuring and recording force or power

$fn = 50;

// Main parameters
body_length = 80;
body_width = 25;
body_height = 15;
handle_diameter = 12;
handle_length = 60;
screen_width = 30;
screen_height = 20;
button_rows = 3;
button_cols = 2;

// Main body
module main_body() {
    color("silver") {
        // Main casing
        translate([0, 0, body_height/2])
            cube([body_length, body_width, body_height], center=true);

        // Front bevel
        translate([body_length/2 - 5, 0, body_height/2])
            rotate([0, 90, 0])
                cylinder(h=body_width, r1=5, r2=0);
    }
}

// Handle with grip texture
module handle() {
    color("darkslategray") {
        // Main handle cylinder
        rotate([90, 0, 0])
            cylinder(h=handle_length, d=handle_diameter);

        // Grip rings
        for(i = [0:5]) {
            translate([0, 0, i * 10 - 25])
                rotate([90, 0, 0])
                    cylinder(h=2, d=handle_diameter + 2);
        }

        // End cap
        translate([0, 0, -handle_length/2 - 2])
            rotate([90, 0, 0])
                cylinder(h=4, d=handle_diameter + 4);
    }
}

// LCD screen
module screen() {
    color("black") {
        translate([-body_length/4, 0, body_height + 1])
            cube([screen_width, screen_height, 1], center=true);
    }

    // Screen bezel
    color("dimgray") {
        translate([-body_length/4, 0, body_height + 0.5])
            cube([screen_width + 4, screen_height + 4, 2], center=true);
    }
}

// Control buttons
module buttons() {
    for(row = [0:button_rows-1]) {
        for(col = [0:button_cols-1]) {
            translate([
                body_length/4 + col * 8 - 4,
                body_width/4 + row * 8 - 8,
                body_height + 1
            ]) {
                color("red") {
                    cylinder(h=2, d=4);
                }
            }
        }
    }
}

// Measurement probe connector
module probe_connector() {
    color("gold") {
        translate([body_length/2 + 2, 0, body_height/2])
            cylinder(h=8, d1=6, d2=4);

        // Threads
        for(i = [0:3]) {
            translate([body_length/2 + 4, 0, body_height/2 - 4 + i * 2])
                rotate([0, 90, 0])
                    cylinder(h=0.5, d=6.5);
        }
    }
}

// Battery compartment cover
module battery_cover() {
    color("black") {
        difference() {
            translate([0, -body_width/2 - 0.5, 2])
                cube([30, 1, 6], center=true);

            // Screws
            translate([-10, -body_width/2 - 0.5, 2])
                cylinder(h=2, d=2);
            translate([10, -body_width/2 - 0.5, 2])
                cylinder(h=2, d=2);
        }
    }
}

// Label area
module label() {
    color("white") {
        translate([-body_length/4, body_width/2 + 0.5, body_height/2])
            cube([20, 0.5, 8], center=true);
    }
}

// Assembly
main_body();
translate([0, 0, -body_height/2 - handle_length/2])
    handle();
screen();
buttons();
probe_connector();
battery_cover();
label();

// OpenSCAD Ergometer (Rowing Machine) - Realistic Style, Medium Complexity
