// Springmeter (spring scale) - Realistic style, medium complexity
// Overall dimensions: ~80mm length, ~20mm width

// Parameters
$fn = 32;
spring_length = 60;
spring_diameter = 8;
body_width = 18;
body_height = 12;
hook_diameter = 3;
hook_length = 15;
scale_marking_length = 6;

// Colors
color_body = [0.2, 0.2, 0.2];
color_spring = [0.8, 0.8, 0.8];
color_hook = [0.7, 0.7, 0.7];
color_markings = [1, 1, 1];

// Main body assembly
union() {
    // Main body tube
    color(color_body)
    difference() {
        // Outer tube
        cylinder(h = spring_length, r = body_width/2, center = true);

        // Inner hollow
        cylinder(h = spring_length + 1, r = body_width/2 - 2, center = true);
    }

    // Top end cap
    translate([0, 0, spring_length/2])
    color(color_body)
    cylinder(h = 4, r = body_width/2 + 1, center = false);

    // Bottom end cap with hook hole
    translate([0, 0, -spring_length/2 - 4])
    color(color_body)
    difference() {
        cylinder(h = 4, r = body_width/2 + 1, center = false);
        // Hook hole
        translate([0, 0, -1])
        cylinder(h = 6, r = hook_diameter/2 + 0.5, center = false);
    }

    // Measurement scale window
    translate([body_width/2 - 1, 0, 0])
    rotate([0, 90, 0])
    difference() {
        // Window frame
        cube([spring_length, body_height, 2], center = true);

        // Window opening
        translate([0, 0, -1])
        cube([spring_length - 4, body_height - 4, 3], center = true);
    }

    // Scale markings
    for(i = [0:5]) {
        translate([body_width/2, -body_height/2 + 2 + i*2, -spring_length/2 + 5 + i*10])
        color(color_markings)
        cube([scale_marking_length, 0.5, 1]);
    }

    // Spring assembly
    translate([0, 0, -spring_length/2 + 2])
    spring_coil(spring_length - 4, spring_diameter, 8);

    // Top hook
    translate([0, 0, spring_length/2 + 2])
    hook_assembly();

    // Bottom hook
    translate([0, 0, -spring_length/2 - 2])
    rotate([180, 0, 0])
    hook_assembly();

    // Grip texture rings
    for(i = [0:4]) {
        translate([0, 0, -spring_length/2 + 8 + i*10])
        color([0.1, 0.1, 0.1])
        difference() {
            cylinder(h = 2, r = body_width/2 + 0.5, center = true);
            cylinder(h = 3, r = body_width/2 - 1, center = true);
        }
    }
}

// Spring coil module
module spring_coil(height, diameter, coils) {
    color(color_spring)
    for(i = [0:coils*8]) {
        rotate([0, 0, i * 360/(coils*8)])
        translate([diameter/2 * cos(i * 360/(coils*8)), 
                   diameter/2 * sin(i * 360/(coils*8)), 
                   i * height/(coils*8)])
        sphere(d = 1.2, $fn = 8);
    }
}

// Hook assembly module
module hook_assembly() {
    color(color_hook)
    union() {
        // Hook stem
        cylinder(h = 5, r = hook_diameter/2);

        // Hook curve
        translate([0, hook_diameter, -hook_length/2])
        rotate([90, 0, 0])
        difference() {
            torus(hook_diameter*2, hook_diameter/2);
            // Cut for opening
            translate([0, -hook_diameter, 0])
            cube([hook_diameter*4, hook_diameter*2, hook_diameter*2], center = true);
        }
    }
}

// Torus helper for hook curve
module torus(outer_r, inner_r) {
    rotate_extrude()
    translate([outer_r, 0, 0])
    circle(r = inner_r);
}

