                translate([-body_length/2+5, 0, body_height/2])
                    rotate([0, 90, 0])
                        cylinder(h=body_width+2, r=5, $fn=20);
            }

        // Reinforcement ribs
        for(i = [-30:20:30]) {
            translate([i, 0, -body_height/4])
