// OpenSCAD Accelerometer Tool – 3-axis MEMS breakout, medium complexity
// Overall size ~70 mm × 40 mm × 18 mm, 3D-printable (no steep overhangs)

// ------------------ Parameters ------------------
board_w      = 60;      // PCB width (mm)
board_l      = 30;      // PCB length
board_t      = 1.6;     // PCB thickness
hole_d       = 2.2;     // Mounting hole diameter
hole_margin  = 2.5;     // Distance from edge to hole
chip_size    = 5;       // Accelerometer IC body size
header_pitch = 2.54;    // Standard 0.1" header pitch
header_pins  = 6;       // 6-pin right-angle header
header_len   = 6;       // Pin length protruding under board
wall_t       = 1.2;     // Safe print wall thickness

// ------------------ Colors ----------------------
pcb_color   = "green";
chip_color  = "black";
pin_color   = "silver";
silk_color  = "white";
mask_color  = "blue";

// ------------------ PCB with holes --------------
module pcb() {
    color(pcb_color)
    difference() {
        // Main board
        translate([0, 0, board_t/2])
            cube([board_w, board_l, board_t], center=true);

        // Mounting holes (4 corners)
        for (x = [-1, 1], y = [-1, 1])
            translate([x*(board_w/2 - hole_margin),
                       y*(board_l/2 - hole_margin),
                       0])
                cylinder(d=hole_d, h=board_t+0.1, center=true, $fn=16);
    }
}

// ------------------ 6-pin right-angle header ----
module header() {
    color(pin_color)
    for (i = [0:header_pins-1]) {
        // Vertical pin
        translate([board_w/2 - 3 - i*header_pitch,
                   board_l/2,
                   board_t + header_len/2])
            cylinder(d=0.64, h=header_len, $fn=8);
        // Horizontal pin (right angle)
        translate([board_w/2 - 3 - i*header_pitch,
                   board_l/2 + header_len/2,
                   board_t])
            rotate([90, 0, 0])
                cylinder(d=0.64, h=header_len, $fn=8);
    }
}

// ------------------ Accelerometer IC ------------
module ic() {
    color(chip_color)
    translate([0, 0, board_t + chip_size/2])
        cube([chip_size, chip_size, chip_size], center=true);

    // Tiny fake bond pads
    color(silk_color)
    for (tx = [-1, 1], ty = [-1, 1])
        translate([tx*chip_size/3, ty*chip_size/3, board_t + chip_size + 0.1])
            cube([0.5, 0.5, 0.2], center=true);
}

// ------------------ Silkscreen labels -----------
module silk() {
    color(silk_color)
    translate([board_w/2 - 12, board_l/2 - 5, board_t + 0.1])
        cube([10, 2, 0.2]);
    translate([-board_w/2 + 4, board_l/2 - 5, board_t + 0.1])
        cube([8, 2, 0.2]);
}

// ------------------ Solder-mask dots (vias) -----
module mask_dots() {
    color(mask_color)
    for (i = [1, 3, 5])
        translate([board_w/2 - 4 - i*header_pitch,
                   board_l/2 - 8,
                   board_t + 0.1])
            cylinder(d=0.8, h=0.2, $fn=8);
}

// ------------------ Main assembly ---------------
