// Realistic 3D-printable gyroscope tool – medium complexity
// Overall size ≈ 85 mm Ø × 30 mm thick, wall 2 mm, no steep overhangs

$fn = 64;

// --- Adjustable parameters ---------------------------------------------------
ring_od   = 80;     // outer ring diameter
ring_id   = 76;     // inner ring diameter
axle_d    = 6;      // gimbal axle diameter
handle_l  = 25;     // handle length
handle_d  = 12;     // handle diameter
grip_d    = 16;     // grip collar diameter
grip_r    = 1;      // grip ridge radius
// -----------------------------------------------------------------------------

// Colors
color_ring   = [0.7,0.7,0.7];   // aluminium
color_axle   = [0.5,0.5,0.5];   // steel
color_spin   = [0.9,0.1,0.1];   // red rotor
color_handle = [0.2,0.2,0.2];   // black grip

// Helper: thin ring with rectangular cross-section
module thin_ring(od,id,h){
    difference(){
        cylinder(d=od,h=h,center=true);
        cylinder(d=id,h=h+0.2,center=true); // small clearance
    }
}

// Rotor disc (flywheel)
module rotor(){
    color(color_spin){
        cylinder(d=ring_id-6,h=6,center=true);
        // spokes for realism – printable from bed
        for(i=[0:60:359]){
            rotate([0,0,i])
                translate([ring_id/2-3,0,0])
                    cube([6,2,6.2],center=true);
        }
    }
}

// Handle with grip ridges
module handle(){
    color(color_handle){
        // main handle body
        cylinder(d=handle_d,h=handle_l,center=false);
        // grip ridges
        for(z=[5:5:handle_l-5]){
            translate([0,0,z])
                cylinder(d=grip_d,h=2,center=false);
        }
        // smooth base
        translate([0,0,handle_l])
            cylinder(d=handle_d,h=2,$fn=32);
    }
}

// Gimbal axle pin
module axle(len){
    color(color_axle){
        cylinder(d=axle_d,h=len,center=true);
        // small heads for press-fit
        translate([0,0,len/2])
            sphere(d=axle_d+2);
        translate([0,0,-len/2])
            sphere(d=axle_d+2);
    }
}

// --- Main assembly ----------------------------------------------------------
difference(){
    union(){
        // Outer gimbal ring
        color(color_ring) rotate([90,0,0])
            thin_ring(ring_od,ring_id,4);

        // Inner gimbal ring (perpendicular to outer)
        color(color_ring) rotate([0,90,0])
            thin_ring(ring_od-10,ring_od-14,4);

        // Rotor inside inner ring
        rotate([0,0,0]) rotor();

        // Axles between rings
        color(color_axle){
            translate([0,ring_od/2-2,0])
                rotate([90,0,0])
                    axle(6);
            translate([0,-ring_od/2+2,0])
                rotate([90,0,0])
                    axle(6);
        }

        // Handle attached to outer ring
        translate([0,ring_od/2+handle_l/2+3,0])
            rotate([90,0,0])
                handle();
    }

    // Cutouts for press-fit axle holes (simple cylinder subtractions)
    translate([0,ring_od/2-2,0])
        rotate([90,0,0])
            cylinder(d=axle_d+0.4,h=10,center=true);
    translate([0,-ring_od/2+2,0])
        rotate([90,0,0])
            cylinder(d=axle_d+0.4,h=10,center=true);
    translate([0,0,ring_od/2-2])
        rotate([0,90,0])
            cylinder(d=axle_d+0.4,h=10,center=true);
    translate([0,0,-ring_od/2+2])
        rotate([0,90,0])
            cylinder(d=axle_d+0.4,h=10,center=true);
}

