// Magnetometer (compass) tool - realistic style with medium complexity
// Dimensions in millimeters, suitable for 3D printing

// Main colors
$fn = 32;

// Main body dimensions
body_diameter = 50;
body_height = 10;

// Needle dimensions
needle_length = 40;
needle_width = 3;
needle_thickness = 1.5;

// Lid dimensions
lid_thickness = 2;
lid_clearance = 0.2;

// Handle dimensions
handle_length = 15;
handle_diameter = 8;

// Scale markings
scale_marks = 24;
scale_radius = 22;

// Main magnetometer module
module magnetometer() {
    color([0.8, 0.8, 0.8]) {
        // Base body - cylindrical housing
        cylinder(h=body_height, d=body_diameter, $fn=64);

        // Raised rim for lid seating
        translate([0, 0, body_height - 1])
            cylinder(h=1, d1=body_diameter, d2=body_diameter - 2, $fn=64);
    }

    // Lid - removable top
    color([0.9, 0.9, 0.9]) {
        translate([0, 0, body_height + lid_clearance]) {
            // Lid base
            cylinder(h=lid_thickness, d=body_diameter - 2, $fn=64);

            // Lid rim
            difference() {
                cylinder(h=lid_thickness, d=body_diameter, $fn=64);
                translate([0, 0, -0.1])
                    cylinder(h=lid_thickness + 0.2, d=body_diameter - 4, $fn=64);
            }

            // Central dome
            translate([0, 0, lid_thickness]) {
                sphere(d=10);
            }
        }
    }

    // Needle assembly
    translate([0, 0, body_height/2 + 1]) {
        // Needle pivot post
        color([0.5, 0.5, 0.5]) {
            cylinder(h=3, d=3, $fn=16);
        }

        // Magnetic needle - red end for north
        translate([0, 0, 1.5]) {
            rotate([0, 0, $t * 360]) {
                // North end (red)
                color([0.8, 0, 0]) {
                    translate([0, needle_length/4, 0])
                        cube([needle_width, needle_length/2, needle_thickness], center=true);
                }

                // South end (white)
                color([0.9, 0.9, 0.9]) {
                    translate([0, -needle_length/4, 0])
                        cube([needle_width, needle_length/2, needle_thickness], center=true);
                }

                // Central hub
                color([0.4, 0.4, 0.4]) {
                    cylinder(h=needle_thickness, d=6, $fn=32);
                }
            }
        }
    }

    // Scale markings
    color([0, 0, 0]) {
        for(i = [0:scale_marks-1]) {
            rotate([0, 0, i * 360/scale_marks]) {
                translate([scale_radius, 0, body_height - 0.5]) {
                    if(i % 6 == 0) {
                        // Cardinal points
                        cube([1, 4, 0.8], center=true);
                    } else if(i % 3 == 0) {
                        // Ordinal points
                        cube([0.8, 3, 0.6], center=true);
                    } else {
                        // Minor marks
                        cube([0.5, 2, 0.4], center=true);
                    }
                }
            }
        }
    }

    // Cardinal direction labels (N, E, S, W)
    color([0.1, 0.1, 0.1]) {
        translate([0, scale_radius + 6, body_height - 0.5]) {
            cube([2, 2, 0.5], center=true); // N
        }
        rotate([0, 0, 90])
            translate([0, scale_radius + 6, body_height - 0.5]) {
                cube([2, 2, 0.5], center=true); // E
            }
        rotate([0, 0, 180])
            translate([0, scale_radius + 6, body_height - 0.5]) {
                cube([2, 2, 0.5], center=true); // S
            }
        rotate([0, 0, 270])
            translate([0, scale_radius + 6, body_height - 0.5]) {
                cube([2, 2, 0.5], center=true); // W
            }
    }

    // Handle attachment
    color([0.7, 0.7, 0.7]) {
        translate([body_diameter/2 + handle_length/2, 0, body_height/2]) {
            rotate([0, 90, 0]) {
                cylinder(h=handle_length, d=handle_diameter, $fn=32);
            }

            // Handle grip texture
            for(i = [0:3]) {
                rotate([0, 90, i * 90]) {
                    translate([handle_length/2 - 2, 0, handle_diameter/2 + 0.5]) {
                        cube([4, 1, 1], center=true);
                    }
                }
            }
        }
    }

    // Sighting notch
    color([0.6, 0.6, 0.6]) {
        translate([0, body_diameter/2, body_height/2]) {
            difference() {
                cube([6, 2, 6], center=true);
                translate([0, 1, 0])
                    cube([4, 2, 4], center=true);
            }
        }
    }

    // Sighting post
    translate([0, -body_diameter/2 - 2, body_height/2]) {
        color([0.6, 0.6, 0.6]) {
            cube([2, 4, 2], center=true);
        }
    }
}

