    difference() {
        cylinder(d=compass_diameter, h=compass_thickness, $fn=64);

        // Cardinal points N, S, E, W
        color("red")
        for(dir=[0, 90, 180, 270]) {
            rotate([0, 0, dir])
            translate([0, compass_diameter/2 - 8, compass_thickness/2])
