        cube([needle_length/2, needle_width, needle_thickness], center=true);

        // South end (white)
        color("white")
        translate([-needle_length/4 - needle_length/8, 0, 0])
        cube([needle_length/2, needle_width, needle_thickness], center=true);

        // Center pivot
        color("gold")
        cylinder(d=3, h=needle_thickness + 0.2, $fn=16);
