// Helper module for rounded cube
module rounded_cube(size, radius) {
    hull() {
        for (i = [0, 1], j = [0, 1], k = [0, 1]) {
            translate([i * (size[0] - 2 * radius), j * (size[1] - 2 * radius), k * (size[2] - 2 * radius)])
                sphere(radius, $fn=16);
        }
    }
}

