        translate([30 - i * 15, -scope_depth/2 - 3, 8])
            cylinder(h=3, r=4, $fn=20);
        color(color_body)
        translate([30 - i * 15, -scope_depth/2 - 3.5, 8])
            cylinder(h=1, r=3, $fn=20);
    }

    // Channel inputs
    for (i = [0:1]) {
        color(color_accent)
        translate([-30 + i * 20, -scope_depth/2 - 2, 5])
            cylinder(h=2, r=2.5, $fn=16);
        translate([-30 + i * 20, -scope_depth/2 - 2.5, 5])
            cylinder(h=1, r=2, $fn=16);
