    translate([35, -scope_depth/2 - 2, 5])
        cylinder(h=2, r=3, $fn=16);

    // Carrying handle
    color(color_body)
    translate([0, 0, scope_height + 5])
        difference() {
            // Handle arch
            rotate([90, 0, 0])
                cylinder(h=scope_width - 10, r=handle_radius, center=true, $fn=20);
            // Hollow handle
            rotate([90, 0, 0])
                cylinder(h=scope_width - 8, r=handle_radius - 2, center=true, $fn=20);
        }

    // Feet
    color(color_body)
    for (x = [-1, 1])
        for (y = [-1, 1])
            translate([x * 30, y * 25, -2])
                cylinder(h=2, r=3, $fn=16);

    // Ventilation slots
    color(color_body)
    for (i = [0:4]) {
        translate([0, scope_depth/2 - 5 - i * 8, scope_height/2])
            cube([scope_width - 10, 1, 3], center=true);
    }

    // Brand label
    color(color_text)
    translate([0, -scope_depth/2 - 2, scope_height - 8])
        cube([25, 0.5, 6], center=true);
}

