// Ring Tool - OpenSCAD Code
// A realistic ring mandrel with sizing grooves and measurement marks

// Parameters
mandrel_length = 80;
base_diameter = 25;
tip_diameter = 16;
handle_length = 30;
handle_diameter = 20;
groove_depth = 0.5;
groove_width = 1.2;
marking_depth = 0.2;
wall_thickness = 2;

// Colors
color_metal = [0.7, 0.7, 0.75];
color_grip = [0.2, 0.2, 0.2];
color_markings = [0.1, 0.1, 0.1];

// Main module
module ring_tool() {
    union() {
        // Handle
        color(color_grip)
        translate([0, 0, 0])
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Handle grip texture
        color(color_grip)
        for(i = [0:5:handle_length-5]) {
            translate([0, 0, i])
            difference() {
                cylinder(h=5, d=handle_diameter, $fn=32);
                cylinder(h=5, d=handle_diameter-4, $fn=32);
            }
        }

        // Mandrel shaft
        color(color_metal)
        translate([0, 0, handle_length])
        cylinder(h=mandrel_length, d1=base_diameter, d2=tip_diameter, $fn=64);

        // Sizing grooves
        color(color_metal)
        for(size = [1:18]) {
            pos = handle_length + (size-1) * (mandrel_length/18);
            dia = base_diameter - (base_diameter-tip_diameter) * (size-1)/18;
            translate([0, 0, pos])
            difference() {
                cylinder(h=groove_width, d=dia, $fn=64);
                cylinder(h=groove_width+0.1, d=dia-2*groove_depth, $fn=64);
            }
        }

        // Size markings
        color(color_markings)
        for(size = [1:2:17]) {
            pos = handle_length + (size-1) * (mandrel_length/18) + groove_width/2;
            dia = base_diameter - (base_diameter-tip_diameter) * (size-1)/18;
            for(angle = [0:120:240]) {
                rotate([0, 0, angle])
                translate([dia/2 - marking_depth, 0, pos])
                cube([marking_depth*2, 1, groove_width], center=true);
            }
        }

        // Base collar
        color(color_metal)
        translate([0, 0, 0])
        difference() {
            cylinder(h=5, d=handle_diameter+6, $fn=32);
            cylinder(h=5.1, d=handle_diameter, $fn=32);
        }

        // Tip detail
        color(color_metal)
        translate([0, 0, handle_length+mandrel_length])
        sphere(d=tip_diameter, $fn=32);
    }
}

// Render the tool
ring_tool();

