color_metal = [0.8, 0.8, 0.8];

// Base with measurement markings
module base() {
    color(color_base)
    union() {
        // Main base block
        cube([base_width, base_depth, base_height]);

        // Front measurement scale
        for(i = [0:5:base_width-5]) {
            translate([i, base_depth-2, base_height-0.1])
            cube([0.5, 2, 0.2]);
        }

        // Base foot pads
        translate([5, 5, -1])
        cylinder(h=2, r=3);
        translate([base_width-5, 5, -1])
        cylinder(h=2, r=3);
        translate([5, base_depth-5, -1])
        cylinder(h=2, r=3);
        translate([base_width-5, base_depth-5, -1])
        cylinder(h=2, r=3);
    }
}

// Vertical column with scale markings
module column() {
    color(color_column)
    union() {
        // Main column
        translate([(base_width-column_width)/2, (base_depth-column_width)/2, base_height])
        cube([column_width, column_width, column_height]);

        // Measurement scale grooves
        for(i = [5:5:column_height-5]) {
            translate([(base_width-column_width)/2, (base_depth-column_width)/2-0.1, base_height+i])
            cube([column_width, 0.2, 0.5]);
            translate([(base_width-column_width)/2-0.1, (base_depth-column_width)/2, base_height+i])
            cube([0.2, column_width, 0.5]);
        }
    }
}

// Sliding measurement arm assembly
module slider() {
    color(color_slider)
    union() {
        // Main slider body
        translate([(base_width-slider_width)/2, (base_depth-slider_thickness)/2, base_height+30])
        cube([slider_width, slider_thickness, slider_width/2]);

        // Digital display housing
        translate([(base_width-slider_width)/2+5, (base_depth-slider_thickness)/2-2, base_height+30+slider_width/2])
        cube([slider_width-10, 4, 8]);

        // Display screen
        color(color_display)
        translate([(base_width-slider_width)/2+6, (base_depth-slider_thickness)/2-1.9, base_height+30+slider_width/2+1])
        cube([slider_width-12, 3.8, 6]);

        // Slider clamp mechanism
        translate([(base_width-column_width)/2-2, (base_depth-4)/2, base_height+30])
        cube([column_width+4, 4, 8]);

        // Thumb wheel for adjustment
        color(color_metal)
        translate([(base_width-column_width)/2-3, (base_depth-4)/2-1, base_height+34])
        rotate([90, 0, 0])
        cylinder(h=6, r=3, $fn=12);
    }
}

// Measurement arm with scribe point
module measurement_arm() {
    color(color_metal)
    union() {
        // Main measurement arm
        translate([(base_width-slider_width)/2, (base_depth-slider_thickness)/2, base_height+30])
        rotate([0, 90, 0])
        cube([measure_arm_length, measure_arm_thickness, slider_thickness]);

        // Fine adjustment wheel
        translate([(base_width-slider_width)/2+measure_arm_length-5, (base_depth-slider_thickness)/2, base_height+30])
        rotate([0, 0, 90])
        cylinder(h=4, r=2, $fn=12);

        // Scribe point
        translate([(base_width-slider_width)/2+measure_arm_length, (base_depth-slider_thickness)/2, base_height+30-measure_arm_thickness/2])
        rotate([0, 0, 90])
        cylinder(h=8, r1=measure_arm_thickness/2, r2=0.5);
    }
}

// Assembly
base();
column();
slider();
measurement_arm();

