// Bore tool - Realistic style, medium complexity
// 3D printable bore gauge tool for measuring hole diameters

// Main parameters
$fn = 32;
handle_length = 80;
handle_diameter = 12;
shaft_length = 40;
shaft_diameter = 8;
head_length = 25;
head_diameter = 16;
measurement_range = 10;

// Main module
module bore_tool() {
    color("silver") {
        // Handle assembly
        handle();

        // Shaft
        shaft();

        // Measuring head with contact points
        measuring_head();

        // Calibration markings
        markings();
    }
}

// Handle with grip texture
module handle() {
    // Main handle body
    cylinder(h=handle_length, d=handle_diameter, center=false);

    // Grip rings
    for(i = [5:10:handle_length-15]) {
        translate([0, 0, i]) {
            difference() {
                cylinder(h=3, d=handle_diameter+1, center=false);
                cylinder(h=3.5, d=handle_diameter-2, center=false);
            }
        }
    }

    // End cap
    translate([0, 0, handle_length]) {
        sphere(d=handle_diameter);
    }
}

// Shaft section
module shaft() {
    translate([0, 0, handle_length]) {
        cylinder(h=shaft_length, d=shaft_diameter, center=false);
    }
}

// Measuring head with adjustable contacts
module measuring_head() {
    translate([0, 0, handle_length+shaft_length]) {
        union() {
            // Head body
            cylinder(h=head_length, d=head_diameter, center=false);

            // Contact rods (3-point measurement)
            for(i = [0:120:240]) {
                rotate([0, 0, i]) {
                    translate([head_diameter/2 + 2, 0, head_length/2]) {
                        rotate([90, 0, 0]) {
                            cylinder(h=measurement_range+5, d=2, center=false);
                        }
                    }
                }
            }

            // Protective collar
            translate([0, 0, -2]) {
                cylinder(h=3, d=head_diameter+4, center=false);
            }
        }
    }
}

// Calibration markings on shaft
module markings() {
    color("black") {
        for(i = [0:5:shaft_length-5]) {
            translate([shaft_diameter/2 + 1, 0, handle_length + i]) {
                rotate([0, 90, 0]) {
                    cube([0.5, 5, 0.3], center=true);
                }
            }
        }

        // Main measurement scale
        for(i = [0:2:measurement_range]) {
            translate([0, 0, handle_length+shaft_length+head_length+i+2]) {
                rotate([90, 0, 0]) {
                    color("red") cylinder(h=0.5, d=15, center=true);
                }
            }
        }
    }
}

// Assembly
bore_tool();

// OpenSCAD Inside Caliper (Vernier Caliper - Inside Jaws)
// Medium complexity realistic style
// Measures internal diameters and grooves
// Designed for 3D printing - no overhangs

// Color definitions
$fn = 64;

// Main module
module inside_caliper() {
    // Main beam
    color("silver")
    difference() {
        union() {
            // Beam body
            translate([-5, -3, 0])
                cube([120, 6, 8]);

            // Measurement scale (raised ridges)
            for(i = [0:19]) {
                translate([10 + i*5, 3.1, 0.5])
                    cube([0.2, 0.5, 7]);
            }
        }

        // Vernier scale window
        translate([85, -0.5, 1])
            cube([15, 6.5, 6]);
    }

    // Fixed jaw (left side)
    color("silver")
    union() {
        // Main jaw body
        translate([-15, -8, 0])
            cube([10, 16, 8]);

        // Inside measuring surface
        translate([-15, -8, 0])
            cube([2, 16, 8]);

        // Reinforcement
        translate([-15, -8, 8])
            cube([10, 16, 2]);
    }

    // Sliding jaw assembly
    module sliding_jaw(pos) {
        color("silver")
        union() {
            // Slider body
            translate([pos-3, -8, 0])
                cube([6, 16, 8]);

            // Inside measuring surface
            translate([pos+3, -8, 0])
                cube([2, 16, 8]);

            // Thumb wheel support
            translate([pos-2, 8, 4])
                cube([4, 4, 4]);

            // Thumb wheel
            color("black")
            rotate([90, 0, 0])
                translate([pos, 12, 4])
                    cylinder(h=2, r=4);

            // Vernier scale
            color("black")
            translate([pos+3.5, 0.5, 1])
                cube([10, 0.2, 6]);

            // Vernier marks
            for(i = [0:9]) {
                translate([pos+4 + i*1, 0.5, 2.5])
                    cube([0.1, 0.2, 3]);
            }

            // Locking screw
            color("gray")
            translate([pos-1, 0, 9])
                cylinder(h=3, r=1.5);

            // Locking lever
            translate([pos-1, -2, 12])
                rotate([90, 0, 0])
                    cylinder(h=5, r=0.8);
        }
    }

    // Depth rod (stored position)
    color("silver")
    translate([110, 0, 4])
        rotate([0, 90, 0])
            cylinder(h=50, r=1);

    // Depth rod holder
    translate([108, -2, 2])
        cube([4, 4, 4]);

    // Generate sliding jaw at position 50
    sliding_jaw(50);

    // Grip texture on beam
    color("black")
    difference() {
        translate([20, -4, -0.5])
            cube([60, 8, 1]);

        for(i = [0:5]) {
            translate([25 + i*10, -3.5, -0.4])
                cube([8, 7, 0.8]);
        }
    }
}

// Center the model
translate([-20, 0, 0])
    inside_caliper();

// Garden Trowel - Medium Complexity Realistic Style
// Includes handle grip, blade curvature, and hanging hole

// Overall dimensions
handle_length = 80;
handle_diameter = 22;
blade_length = 60;
blade_width = 45;
blade_thickness = 2.5;

// Handle grip module
module handle_grip() {
    difference() {
        // Main handle cylinder
        color("darkgreen")
        cylinder(h=handle_length, d=handle_diameter, $fn=32);

        // Grip rings (recessed)
        for(i = [10:15:handle_length-20]) {
            translate([0, 0, i])
            cylinder(h=3, d=handle_diameter+2, $fn=32);
        }

        // Hanging hole
        translate([0, handle_diameter/2 + 5, handle_length - 15])
        rotate([90, 0, 0])
        cylinder(h=handle_diameter+2, d=6, $fn=20);
    }
}

// Blade module with realistic scoop shape
module blade() {
    difference() {
        union() {
            // Main blade body - tapered cylinder segment
            translate([0, 0, handle_length])
            rotate([90, 0, 0])
            linear_extrude(height=blade_length, scale=[1, 0.3])
            difference() {
                circle(d=blade_width, $fn=50);
                translate([0, -blade_width/2, 0])
                square([blade_width, blade_width/2]);
            }

            // Blade thickness
            translate([0, 0, handle_length])
            rotate([90, 0, 0])
            linear_extrude(height=blade_length, scale=[1, 0.3])
            offset(r=-blade_thickness)
            difference() {
                circle(d=blade_width, $fn=50);
                translate([0, -blade_width/2, 0])
                square([blade_width, blade_width/2]);
            }

            // Reinforcement ridge
            translate([0, 0, handle_length + blade_length/2])
            rotate([90, 0, 0])
            cube([blade_length, blade_thickness*3, blade_width/10], center=true);
        }

        // Cut scoop cavity
        translate([0, 0, handle_length + blade_length/2])
        rotate([90, 0, 0])
        linear_extrude(height=blade_length*1.1, scale=[1.2, 0.5], center=true)
        circle(d=blade_width*0.9, $fn=40);
    }
}

// Handle to blade transition
module transition() {
    color("darkgreen")
    translate([0, 0, handle_length - 5])
    hull() {
        cylinder(h=10, d1=handle_diameter, d2=blade_width*0.4, $fn=32);
        translate([0, 0, 10])
        cylinder(h=5, d=blade_width*0.4, $fn=32);
    }
}

// Main assembly
union() {
    handle_grip();
    transition();
    color("silver")
    blade();
}

// Telescoping Utility Knife
// Medium complexity realistic design

$fn = 32;

// Main dimensions
knife_length = 85;
handle_dia = 22;
blade_width = 18;
blade_thickness = 1.2;
telescope_length = 35;

// Colors
handle_color = [0.15, 0.15, 0.15]; // Dark gray
blade_color = [0.7, 0.7, 0.7];   // Silver
grip_color = [0.05, 0.05, 0.05]; // Black
accent_color = [0.8, 0.1, 0.1];  // Red

module grip_texture() {
    difference() {
        scale([1, 1, 1]) sphere(d = handle_dia);
        for (i = [0:7]) {
            rotate([0, 0, i * 45]) 
                translate([-handle_dia/2 - 1, -2, -handle_dia/2])
                    cube([handle_dia + 2, 4, handle_dia]);
        }
    }
}

module handle() {
    color(handle_color) {
        // Main handle body
        difference() {
            cylinder(h = knife_length - telescope_length, d = handle_dia);
            // Internal cavity for telescoping
            translate([0, 0, -1])
                cylinder(h = knife_length - telescope_length + 2, d = handle_dia - 4);
        }

        // Grip pattern
        color(grip_color) {
            translate([0, 0, 5]) grip_texture();
            translate([0, 0, 15]) grip_texture();
            translate([0, 0, 25]) grip_texture();
        }

        // Thumb slider slot
        translate([0, handle_dia/2 - 2, knife_length - telescope_length - 15])
            cube([handle_dia - 8, 4, 15], center = true);
    }
}

module telescoping_section() {
    color(handle_color) {
        difference() {
            cylinder(h = telescope_length, d = handle_dia - 4.5);
            // Internal cavity
            translate([0, 0, -1])
                cylinder(h = telescope_length + 2, d = handle_dia - 8);
        }

        // Thumb slider
        color(accent_color) {
            translate([0, handle_dia/2 - 4.5, 8])
                cube([10, 3, 8], center = true);
        }
    }
}

module blade() {
    color(blade_color) {
        // Main blade body
        translate([0, 0, telescope_length]) {
            difference() {
                cube([blade_width, blade_thickness, 35], center = true);
                // Tapered tip
                translate([0, 0, 17.5])
                    rotate([0, 90, 0])
                        cylinder(h = blade_width, r1 = 0, r2 = blade_width/2);
            }

            // Blade edge
            translate([0, blade_thickness/2 + 0.1, 0])
                cube([blade_width - 2, 0.2, 30], center = true);

            // Score lines for breaking
            for (z = [5:7:30]) {
                translate([0, 0, z])
                    cube([blade_width - 2, blade_thickness + 0.2, 0.5], center = true);
            }
        }
    }
}

// Assembly
handle();

translate([0, 0, knife_length - telescope_length]) {
    telescoping_section();
    blade();
}

// Realistic 3D-printable Divider Tool
// Overall dimensions: ~85 mm long, ~25 mm wide

// Parameters
$fn=50;
blade_len=40;
blade_thick=2;
blade_width=15;
handle_len=35;
handle_dia=12;
pivot_dia=5;
pivot_h=20;
gap=0.2;

// Colors
color_steel=[0.6,0.6,0.6];
color_handle=[0.15,0.15,0.15];

module blade() {
    difference() {
        // Main blade
        color(color_steel)
        linear_extrude(height=blade_thick) {
            polygon(points=[
                [0,0],
                [blade_len,0],
                [blade_len-15, blade_width/2],
                [0, blade_width/2-2]
            ]);
        }
        // Tip bevel
        translate([blade_len-8, blade_width/2-4, 0])
        rotate([0,45,0])
        cube([10, 10, blade_thick+0.1], center=true);

        // Blade edge chamfer
        translate([blade_len/2,0,blade_thick-0.5])
        cube([blade_len+0.2, 0.5, 1], center=true);
    }
}

module handle() {
    difference() {
        // Octagonal grip
        color(color_handle)
        cylinder(h=handle_len, d=handle_dia, $fn=8);

        // Grip rings
        for(i=[1:4]) {
            translate([0,0,i*handle_len/5])
            cylinder(h=2.5, d=handle_dia-2, $fn=8);
        }
    }
}

module pivot_pin() {
    // Pivot cylinder
    cylinder(h=pivot_h, d=pivot_dia, center=true);

    // Washer
    translate([0,0,pivot_h/2+1])
    cylinder(h=1.5, d=pivot_dia+3, center=true);
}

module divider_arm() {
    union() {
        // Handle
        translate([-handle_len-5, 0, 0])
        rotate([0,90,0])
        handle();

        // Blade
        translate([0, 0, -blade_width/2])
        blade();

        // Pivot hole
        translate([0,0,0])
        rotate([90,0,0])
        cylinder(h=20, d=pivot_dia+gap, center=true);
    }
}

module spring() {
    // Simple spring representation
    color([0.7,0.7,0.7])
    for(i=[-2:2]) {
        translate([0, i*2.5, 0])
        rotate([0,90,0])
        cylinder(h=8, d=1.5, center=true);
    }
}

// Main assembly
translate([0, -10, 0])
union() {
    // First arm
    divider_arm();

    // Second arm (mirrored)
    mirror([0,1,0])
    divider_arm();

    // Pivot pin
    translate([0,0,0])
    rotate([90,0,0])
    pivot_pin();

    // Spring between arms
    translate([0,0,10])
    spring();
}

// ------------------------------
// OpenSCAD : 3D-printable Hand Pruner
// Medium-complexity, realistic style
// ------------------------------

// Overall scale factor – makes the pruner ≈ 90 mm long
scale_factor = 1.0;

// Wall thickness for FDM printing
wall = 1.2;

// ---- Colors ----
color_handle = "DarkOliveGreen";
color_blade = "Silver";
color_screw = "DimGray";
color_spring = "GoldenRod";

// ---- Modules ----

// Grip pattern for handles
module grip_pattern(len = 40, dia = 12) {
    n = 12;
    for (i = [0 : n-1])
        rotate([0, 0, i * 360 / n])
            translate([dia / 2 - 0.5, 0, 0])
                cube([1, 1, len], center = true);
}

// Main handle body
module handle_body(len = 60, dia = 12) {
    difference() {
        // Outer shell with slight taper
        cylinder(h = len, d1 = dia, d2 = dia * 0.9, $fn = 36);
        // Hollow core
        translate([0, 0, -1])
            cylinder(h = len + 2, d = dia - 2 * wall, $fn = 36);
    }
    // Grip ridges
    grip_pattern(len - 10, dia - 1);
}

// Blade – curved bypass style
module blade() {
    // Main blade body
    linear_extrude(height = 2.4, center = true, convexity = 4)
        polygon(points = [
            [0, 0],
            [3, 0],
            [40, 12],
            [45, 14],
            [45, 16],
            [42, 17],
            [35, 15],
            [30, 14],
            [25, 13],
            [20, 12],
            [10, 8],
            [0, 0]
        ]);
    // Cutting edge bevel
    translate([0, 0, -1.2])
        linear_extrude(height = 0.6, center = true, convexity = 4)
            polygon(points = [
                [0, 0],
                [3, 0],
                [40, 12],
                [45, 14],
                [45, 15.5],
                [0, 0]
            ]);
}

// Pivot screw / rivet
module pivot_screw() {
    difference() {
        cylinder(h = 10, d = 6, $fn = 20);
        translate([0, 0, -1])
            cylinder(h = 12, d = 3, $fn = 20);
    }
    // Head indent
    translate([0, 0, 5])
        cylinder(h = 1, d = 8, $fn = 6);
}

// Torsion spring (simplified helix)
module spring() {
    spring_r = 4; turns = 2.5; wire_r = 0.8;
    $fn = 8;
    for (i = [0 : 5 : turns * 360])
        rotate([0, 0, i])
            translate([spring_r, 0, i / 5])
                rotate([90, 0, 0])
                    cylinder(h = wire_r * 2, d = wire_r * 2, center = true);
}

// Lower handle with anvil
module lower_handle(len = 60, dia = 12) {
    difference() {
        cylinder(h = len, d1 = dia * 0.9, d2 = dia, $fn = 36);
        translate([0, 0, -1])
            cylinder(h = len + 2, d = dia - 2 * wall, $fn = 36);
    }
    grip_pattern(len - 10, dia - 1);

    // Anvil plate
    translate([0, 0, len])
        rotate([90, 0, 0])
            cube([25, 6, 2], center = true);
}

// ------------------------------
// Assembly
// ------------------------------

// Scale everything
scale(scale_factor) {
    // Lower handle + anvil
    color(color_handle)
        translate([0, 0, 0])
            lower_handle();

    // Upper handle
    color(color_handle)
        rotate([0, 0, 25])
            translate([0, 0, -15])
                handle_body();

    // Pivot
    color(color_screw)
        translate([0, 0, 10])
            pivot_screw();

    // Blade (upper)
    color(color_blade)
        rotate([0, 0, 25])
            translate([0, 0, 0])
                rotate([0, 0, -90])
                    translate([0, 0, 1.2])
                        blade();

    // Spring
    color(color_spring)
        translate([0, 0, 5])
            spring();
}

