// Lawn mower – realistic, medium-complexity, 3D-printable
// Overall length ≈ 80 mm

// ---------- PARAMETERS ----------
blade_d   = 38;      // cutting-disk diameter
blade_h   = 2.5;     // blade thickness
deck_h    = 15;      // mower-deck height
handle_L  = 60;      // handle length
handle_D  = 5;       // handle tube diameter
wheel_D   = 12;      // wheel diameter
wheel_W   = 4;       // wheel width
wall_t    = 1.5;     // printable wall thickness
scale     = 0.8;     // global shrink to fit 80 mm bounding box

// ---------- COLORS ----------
color_deck  = [0,0.6,0.1];
color_metal = [0.6,0.6,0.6];
color_wheel = [0.15,0.15,0.15];
color_grip  = [0.05,0.05,0.05];

// ---------- MODULES ----------
module wheel(pos=[0,0,0]) {
    translate(pos)
    color(color_wheel)
    rotate([90,0,0])
    difference() {
        cylinder(d=wheel_D, h=wheel_W, $fn=20);
        cylinder(d=wheel_D-2*wall_t, h=wheel_W+0.2, $fn=20);
    }
}

module blade() {
    color("silver")
    linear_extrude(height=blade_h, center=true)
    union() {
        for(i=[0:120:359])
            rotate([0,0,i])
            translate([blade_d/2*0.75,0,0])
            cube([blade_d/2,4,1], center=true);
        circle(d=blade_d/4, $fn=32);
    }
}

module deck() {
    color(color_deck)
    union() {
        difference() {
            // main deck shell
            cylinder(d=blade_d+10, h=deck_h, $fn=64);
            cylinder(d=blade_d+10-2*wall_t, h=deck_h+0.2, $fn=64);
        }
        // engine housing bump
        translate([0,0,deck_h])
        cylinder(d=blade_d*0.6, h=8, $fn=32);
    }
}

module handle_bar() {
    color(color_metal)
    union() {
        // lower U
        translate([0,0,deck_h+4])
        rotate([90,0,0])
        difference() {
            cylinder(h=blade_d*0.7, d=handle_D, $fn=16);
            cylinder(h=blade_d*0.7+0.2, d=handle_D-2*wall_t, $fn=16);
        }
        // upright posts
        for(x=[-1,1])
            translate([x*(blade_d*0.35),0,deck_h+4+handle_L/2])
            rotate([0,90,0])
            difference() {
                cylinder(h=handle_L/2, d=handle_D, $fn=16);
                cylinder(h=handle_L/2+0.2, d=handle_D-2*wall_t, $fn=16);
            }
        // upper bar
        translate([0,0,deck_h+4+handle_L])
        rotate([90,0,0])
        difference() {
            cylinder(h=blade_d*0.5, d=handle_D, $fn=16);
            cylinder(h=blade_d*0.5+0.2, d=handle_D-2*wall_t, $fn=16);
        }
        // grips
        color(color_grip)
        for(x=[-1,1])
            translate([x*blade_d*0.25,0,deck_h+4+handle_L])
            rotate([90,0,0])
            cylinder(h=15, d=handle_D+2, $fn=16);
    }
}

// ---------- ASSEMBLY ----------
scale(scale)
union() {
    deck();
    blade();
    handle_bar();
    // wheels
    wheel([ blade_d/2+wheel_W/2, 0, wheel_D/2]);
    wheel([-blade_d/2-wheel_W/2, 0, wheel_D/2]);
    wheel([0,  blade_d/2+wheel_W/2, wheel_D/2]);
    wheel([0, -blade_d/2-wheel_W/2, wheel_D/2]);
}

