bristle_rows = 6;
bristles_per_row = 8;

// Colors
color_handle = [139/255, 69/255, 19/255]; // Brown
color_bristles = [255/255, 248/255, 220/255]; // Beige
color_metal = [192/255, 192/255, 192/255]; // Silver

// Main assembly
module broom() {
    union() {
        // Handle
        color(color_handle)
            handle();

        // Broom head
        translate([0, 0, handle_length])
            color(color_handle)
            broom_head();

        // Metal band
        translate([0, 0, handle_length - 5])
            color(color_metal)
            metal_band();

        // Bristles
        translate([0, 0, handle_length + broom_head_thickness])
            color(color_bristles)
            bristles();
    }
}

// Handle with grip texture
module handle() {
    union() {
        // Main handle cylinder
        cylinder(h = handle_length, d = handle_diameter, $fn = 32);

        // Grip rings
        for(i = [1:5:handle_length-10]) {
            translate([0, 0, i])
                difference() {
                    cylinder(h = 2, d = handle_diameter + 1, $fn = 32);
                    cylinder(h = 2.1, d = handle_diameter, $fn = 32);
                }
        }
    }
}

// Broom head block
module broom_head() {
    difference() {
        // Main block
        cube([broom_head_width, broom_head_length, broom_head_thickness], center = true);

        // Rounded edges
        translate([broom_head_width/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = broom_head_length, d = broom_head_thickness, center = true, $fn = 32);
        translate([-broom_head_width/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = broom_head_length, d = broom_head_thickness, center = true, $fn = 32);
    }
}

// Metal band around handle connection
module metal_band() {
    cylinder(h = 10, d = handle_diameter + 4, center = true, $fn = 32);
}

// Individual bristle
module bristle() {
    cylinder(h = bristle_length, d = bristle_diameter, $fn = 8);
}

// Array of bristles
module bristles() {
    // Calculate spacing
    bristle_spacing_x = broom_head_width / (bristles_per_row + 1);
    bristle_spacing_y = broom_head_length / (bristle_rows + 1);

    // Create grid of bristles
    for(i = [1:bristles_per_row]) {
        for(j = [1:bristle_rows]) {
            translate([
                -broom_head_width/2 + i * bristle_spacing_x,
                -broom_head_length/2 + j * bristle_spacing_y,
                0
            ]) {
                // Slight random angle for realistic look
                rotate([random(i*j) * 5 - 2.5, random(i*j+1) * 5 - 2.5, 0])
                    bristle();
            }
        }
    }
}

// Helper function for random variation
function random(seed) = (seed * 9301 + 49297) % 233280 / 233280;

