// Module for creating knurled grip pattern
module knurled_grip(diameter, length, segments=36) {
    difference() {
        cylinder(h=length, d=diameter, center=false);

        // Create diamond pattern cuts
        for(i=[0:segments-1]) {
            rotate([0, 0, i * 360/segments]) {
                translate([diameter/2 - 1, -1, -1]) {
                    rotate([0, 45, 0]) {
                        cube([2, 2, length + 2]);
                    }
                }
            }
        }

        for(i=[0:segments-1]) {
            rotate([0, 0, i * 360/segments + 180/segments]) {
                translate([diameter/2 - 1, -1, -1]) {
                    rotate([0, -45, 0]) {
                        cube([2, 2, length + 2]);
                    }
                }
            }
        }
    }
}

