        // Main blade body
        difference() {
            // Curved blade shape
            union() {
                linear_extrude(height = blade_thickness)
                    polygon(points = [
                        [0, 0],
                        [blade_length, 0],
                        [blade_length - blade_curve, blade_width/2],
                        [blade_length - blade_curve*2, blade_width],
                        [0, blade_width]
                    ]);

                // Reinforcement ridge
                translate([blade_length/2, blade_width/2, blade_thickness/2])
                    rotate([90, 0, 0])
                        cylinder(h = blade_length/2, d = 5, $fn = 20);
            }

            // Cutting bevel
            translate([blade_length - blade_thickness*2, 0, -0.5])
                cube([blade_thickness*3, blade_width, blade_thickness + 1]);

            // Edge sharpening
            translate([blade_length - blade_thickness, 0, -0.5])
                rotate([0, 0, 30])
                    cube([blade_thickness*2, blade_width, blade_thickness + 1]);
        }

