// Spool for twine
module spool() {
    // Central axle
    cylinder(h=35, d=8, center=true);

    // Left flange
    translate([0, 0, -15])
        cylinder(h=3, d=30);

    // Right flange
    translate([0, 0, 15])
        cylinder(h=3, d=30);

    // Grooves for grip
    for(i = [-12:3:12]) {
        translate([0, 0, i])
            cylinder(h=1, d=28);
    }
}

// Cutting blade
module blade() {
    // Blade base
    translate([30, 0, 15])
        rotate([0, 0, 45])
            cube([20, 2, 5], center=true);

    // Serrated edge
    for(i = [-8:2:8]) {
        translate([30, i, 17])
            cube([1, 1, 3]);
    }
}

// Mounting brackets
module mounts() {
    // Left mount
    translate([-30, -25, -20])
        cube([8, 8, 10]);
    translate([-30, -25, -15])
        cylinder(h=10, d=5);

    // Right mount
    translate([-30, 25, -20])
        cube([8, 8, 10]);
    translate([-30, 25, -15])
        cylinder(h=10, d=5);
}

// Twine guide hole
module guide() {
    translate([30, 0, 0])
        rotate([90, 0, 0])
            cylinder(h=20, d=6);

    // Guide funnel
    translate([30, -10, 0])
        rotate([90, 0, 0])
            cylinder(h=5, d1=10, d2=6);
}

// Assembly
color("gray40") body();
color("burlywood") translate([0, 0, 0]) spool();
color("silver") blade();
color("gray30") mounts();
color("gray60") guide();

// Textured grip pattern on handle
for(i = [-3:1:3]) {
    for(j = [-3:1:3]) {
        color("black")
            translate([-40, i*2, j*2])
                sphere(d=1);
    }
}

// Stakes tool - realistic style, medium complexity
// Overall dimensions: 100mm length, 35mm width, 20mm height

$fn = 32;

// Main colors
color_steel = [0.7, 0.7, 0.75];
color_handle = [0.1, 0.1, 0.1];
color_grip = [0.2, 0.2, 0.2];

// Handle module
module handle() {
    color(color_handle) {
        // Main handle body
        difference() {
            cylinder(h=80, d=25, center=true);
            cylinder(h=82, d=20, center=true);
        }

        // Grip rings
        for(i = [-30:15:30]) {
            translate([0, 0, i]) {
                cylinder(h=4, d1=24, d2=26, center=true);
            }
        }

        // End cap
        translate([0, 0, 40]) {
            cylinder(h=5, d=28);
            cylinder(h=3, d=26);
        }
    }
}

// Stake shaft module
module stake_shaft() {
    color(color_steel) {
        // Main shaft
        difference() {
            cylinder(h=50, d=8, center=true);
            cylinder(h=52, d=6, center=true);
        }

        // Threading
        for(i = [0:2:48]) {
            rotate([0, 0, i * 15]) {
                translate([0, 0, i - 24]) {
                    difference() {
                        cylinder(h=2, d=9, center=true);
                        cylinder(h=3, d=7, center=true);
                    }
                }
            }
        }

        // Point
        translate([0, 0, 25]) {
            cylinder(h=10, d1=8, d2=0);
        }
    }
}

// Adjustment mechanism
module adjustment_mechanism() {
    color(color_steel) {
        // Knurled knob
        translate([0, 0, -30]) {
            cylinder(h=15, d=20);

            // Knurling
            for(i = [0:15:360]) {
                rotate([0, 0, i]) {
                    translate([9.5, 0, 0]) {
                        cube([1, 0.5, 15], center=true);
                    }
                }
            }

            // Center hole
            cylinder(h=16, d=10);
        }

        // Locking collar
        translate([0, 0, -40]) {
            difference() {
                cylinder(h=8, d=15);
                cylinder(h=9, d=8);
            }

            // Set screw
            rotate([0, 90, 0]) {
                translate([7.5, 0, 0]) {
                    cylinder(h=3, d=4);
                }
            }
        }
    }
}

// Assembly
union() {
    // Handle
    handle();

    // Shaft
    translate([0, 0, 45]) {
        rotate([90, 0, 0]) {
            stake_shaft();
        }
    }

    // Adjustment mechanism
    adjustment_mechanism();

    // Safety collar
    color(color_steel) {
        translate([0, 0, -45]) {
            difference() {
                cylinder(h=5, d=30);
                cylinder(h=6, d=18);
            }
        }
    }

    // Measurement markings
    for(i = [0:5:40]) {
        color([0.95, 0.95, 0.95]) {
            translate([12.5, 0, -40 + i]) {
                cube([3, 0.5, 0.5]);
            }
        }
    }
}

// Garden Trellis Tool - Realistic Style, Medium Complexity

// Parameters for realistic scaling
trellis_height = 80;
trellis_width = 60;
post_diameter = 4;
crossbar_diameter = 2.5;
wall_thickness = 1.5;

// Main trellis structure
module trellis_frame() {
    color("forestgreen") {
        // Left vertical post
        translate([-trellis_width/2 + post_diameter/2, 0, trellis_height/2]) 
            cylinder(h=trellis_height, d=post_diameter, $fn=16);

        // Right vertical post
        translate([trellis_width/2 - post_diameter/2, 0, trellis_height/2]) 
            cylinder(h=trellis_height, d=post_diameter, $fn=16);

        // Top crossbar
        translate([0, 0, trellis_height - crossbar_diameter/2]) 
            rotate([90,0,0]) cylinder(h=trellis_width, d=crossbar_diameter, $fn=16);

        // Bottom crossbar
        translate([0, 0, crossbar_diameter/2]) 
            rotate([90,0,0]) cylinder(h=trellis_width, d=crossbar_diameter, $fn=16);
    }
}

// Horizontal support wires
module horizontal_wires() {
    color("silver") {
        for (i = [1:7]) {
            translate([0, 0, i * (trellis_height/8)]) 
                rotate([90,0,0]) cylinder(h=trellis_width - post_diameter, d=1, $fn=8);
        }
    }
}

// Vertical support wires
module vertical_wires() {
    color("silver") {
        for (i = [1:5]) {
            x_pos = -trellis_width/2 + post_diameter + i * ((trellis_width - 2*post_diameter)/6);
            translate([x_pos, 0, trellis_height/2]) 
                cylinder(h=trellis_height - crossbar_diameter, d=1, $fn=8);
        }
    }
}

// Ground spikes for insertion into soil
module ground_spikes() {
    color("darkgreen") {
        // Left spike
        translate([-trellis_width/2 + post_diameter/2, 0, -5]) 
            cylinder(h=10, d1=post_diameter, d2=1, $fn=16);

        // Right spike
        translate([trellis_width/2 - post_diameter/2, 0, -5]) 
            cylinder(h=10, d1=post_diameter, d2=1, $fn=16);
    }
}

// Support brackets for reinforcement
module support_brackets() {
    color("forestgreen") {
        // Left bracket
        translate([-trellis_width/2 + post_diameter/2, -2, crossbar_diameter + 5]) 
            rotate([0,45,0]) cube([5, 1, 5]);

        // Right bracket
        translate([trellis_width/2 - post_diameter/2, -2, crossbar_diameter + 5]) 
            rotate([0,-45,0]) cube([5, 1, 5]);
    }
}

// Tool handle grip
module handle_grip() {
    color("black") {
        translate([0, 0, trellis_height + 15]) 
            cylinder(h=20, d=6, $fn=32);

        // Grip texture
        for (i = [0:3]) {
            translate([0, 0, trellis_height + 15 + i*5]) 
                rotate([90,0,0]) cylinder(h=6.1, d=0.5, $fn=32);
        }
    }
}

// Assembly
union() {
    trellis_frame();
    horizontal_wires();
    vertical_wires();
    ground_spikes();
    support_brackets();
    handle_grip();
}

// Mesh Strainer - Realistic Style, Medium Complexity
// Overall dimensions: 80mm diameter x 40mm height

// Main strainer bowl
module strainer_bowl() {
    // Outer bowl
    difference() {
        cylinder(h=30, d=80, $fn=100);
        translate([0, 0, 5])
            cylinder(h=25, d=76, $fn=100);
    }

    // Rim reinforcement
    translate([0, 0, 28])
        cylinder(h=5, d=82, $fn=100);
}

// Mesh holes pattern
module mesh_holes() {
    // Create array of holes in hexagonal pattern
    spacing = 3.5;
    rows = 8;
    cols = 12;

    for (r = [-rows:rows]) {
        for (c = [-cols:cols]) {
            x_offset = (r % 2) * spacing/2;
            x = c * spacing + x_offset;
            y = r * spacing * 0.866;

            if (sqrt(x*x + y*y) < 34) {
                translate([x, y, 15])
                    cylinder(h=20, d=2.5, $fn=16);
            }
        }
    }
}

// Handle grip pattern
module grip_pattern() {
    rings = 8;
    for (i = [0:rings-1]) {
        translate([0, 0, i * 4 + 2])
            rotate([90, 0, 0])
                cylinder(h=1, d=14, $fn=60);
    }
}

// Main handle
module handle() {
    // Handle shaft
    translate([-50, 0, 15])
        rotate([90, 0, 0])
            cylinder(h=50, d=12, $fn=32);

    // Handle grip
    translate([-50, 0, 15])
        rotate([90, 0, 0])
            difference() {
                cylinder(h=35, d=14, $fn=32);
                grip_pattern();
            }

    // Handle end cap
    translate([-50, 0, 0])
        sphere(d=14, $fn=32);
}

// Reinforcement ribs
module ribs() {
    for (i = [0:5]) {
        rotate([0, 0, i * 60])
            translate([0, 39, 15])
                cube([2, 1, 25], center=true);
    }
}

// Assembly
color("silver") {
    difference() {
        // Main strainer body
        union() {
            strainer_bowl();
            handle();
            ribs();
        }

        // Mesh holes
        mesh_holes();
    }
}

// Netting Tool - Realistic Style, Medium Complexity
// A handheld netting tool with folding handle and replaceable net

$fn = 50;

// Main parameters
handle_length = 80;
handle_diameter = 12;
ring_diameter = 45;
net_depth = 25;
blade_width = 8;

// Colors
handle_color = "SaddleBrown";
metal_color = "Silver";
net_color = "DarkGray";

// Handle module with grip texture
module handle() {
    color(handle_color)
    difference() {
        // Main handle cylinder
        cylinder(h=handle_length, d=handle_diameter, center=false);

        // Grip indentations
        for(i = [10:15:handle_length-20]) {
            translate([0, 0, i])
            rotate([0, 90, 0])
            cylinder(h=handle_diameter+2, d=2, center=true);
        }
    }

    // End cap
    translate([0, 0, handle_length])
    color(metal_color)
    cylinder(h=3, d=handle_diameter+2, center=false);
}

// Hinge mechanism for folding handle
module hinge() {
    color(metal_color) {
        // Fixed part
        difference() {
            cylinder(h=8, d=10, center=true);
            translate([0, 0, -5])
            cube([6, 6, 10], center=true);
        }

        // Rotating pin
        translate([0, 0, 5])
        cylinder(h=12, d=4, center=true);
    }
}

// Netting ring with blade edge
module netting_ring() {
    color(metal_color) {
        // Main ring
        difference() {
            cylinder(h=3, d=ring_diameter, center=true);
            cylinder(h=4, d=ring_diameter-blade_width*2, center=true);
        }

        // Blade edge
        translate([0, 0, 2])
        cylinder(h=1, d=ring_diameter+2, center=true);

        // Attachment points for net
        for(angle = [0:45:315]) {
            rotate([0, 0, angle])
            translate([ring_diameter/2 - blade_width, 0, 0])
            sphere(d=2);
        }
    }
}

// Net mesh (simplified representation)
module net_mesh() {
    color(net_color) {
        // Net ring attachment
        translate([0, 0, -net_depth/2])
        cylinder(h=net_depth, d=ring_diameter-blade_width*2-4, center=true);

        // Cross pattern for net
        for(i = [-1, 1]) {
            rotate([0, 90*i, 0])
            cylinder(h=ring_diameter-blade_width*2-4, d=1, center=true);
        }

        // Diagonal supports
        for(angle = [45:90:315]) {
            rotate([0, 0, angle])
            translate([0, 0, -net_depth/2])
            cylinder(h=net_depth, d=0.5, center=true);
        }
    }
}

// Locking mechanism for handle
module lock_mechanism() {
    color(metal_color) {
        translate([0, handle_diameter/2+3, handle_length-10])
        difference() {
            cube([handle_diameter, 6, 15], center=true);
            translate([0, 0, 5])
            cylinder(h=16, d=3, center=true);
        }

        // Lock button
        translate([0, handle_diameter/2+6, handle_length-5])
        sphere(d=4);
    }
}

// Assembly
// Handle in folded position
translate([0, 0, 0])
handle();

// Hinge connecting handle to ring
translate([0, 0, handle_length])
rotate([90, 0, 0])
hinge();

// Netting ring assembly
translate([0, 0, handle_length+25])
rotate([90, 0, 0])
union() {
    netting_ring();
    translate([0, 0, 2])
    net_mesh();
}

// Lock mechanism on handle
lock_mechanism();

// Tarp Tool - Medium Complexity Realistic Style
// A compact tarp grommet installation tool for 3D printing

// Parameters
$fn = 32;
handle_length = 80;
handle_diameter = 12;
head_length = 25;
head_width = 18;
head_height = 8;
grommet_diameter = 6;
grommet_height = 3;
grip_ring_count = 6;
grip_ring_depth = 1;
color_main = "DarkSlateGray";
color_grip = "Black";
color_metal = "Silver";

// Main handle with grip texture
module handle() {
    color(color_main) {
        // Main handle cylinder
        cylinder(h=handle_length, d=handle_diameter, center=false);

        // Grip rings
        for (i = [1:grip_ring_count]) {
            translate([0, 0, (handle_length/(grip_ring_count+1)) * i])
                difference() {
                    cylinder(h=grip_ring_depth*2, d=handle_diameter+2, center=true);
                    cylinder(h=grip_ring_depth*2+0.1, d=handle_diameter-1, center=true);
                }
        }
    }
}

// Tool head with grommet press
module tool_head() {
    color(color_main) {
        // Main head block
        translate([0, 0, handle_length])
            cube([head_width, head_length, head_height], center=true);

        // Reinforcement ribs
        translate([0, head_length/2 - 2, handle_length])
            cube([head_width-4, 4, head_height-2], center=true);

        translate([0, -head_length/2 + 2, handle_length])
            cube([head_width-4, 4, head_height-2], center=true);
    }
}

// Grommet pressing die (male part)
module male_die() {
    color(color_metal) {
        translate([0, head_length/2 - 1, handle_length + head_height/2])
            cylinder(h=grommet_height, d=grommet_diameter, center=true);

        // Alignment pin
        translate([0, head_length/2 - 1, handle_length + head_height/2 + grommet_height/2])
            cylinder(h=2, d=2, center=true);
    }
}

// Grommet receiving die (female part)
module female_die() {
    color(color_metal) {
        translate([0, -head_length/2 + 1, handle_length + head_height/2])
            difference() {
                cylinder(h=grommet_height, d=grommet_diameter+2, center=true);
                cylinder(h=grommet_height+0.1, d=grommet_diameter, center=true);
            }
    }
}

// Grip texture on handle ends
module handle_grip() {
    color(color_grip) {
        // Top grip
        translate([0, 0, handle_length + head_height/2 + 2])
            rotate([90, 0, 0])
                cylinder(h=8, d=handle_diameter-2, center=true);

        // Bottom grip
        translate([0, 0, -2])
            rotate([90, 0, 0])
                cylinder(h=8, d=handle_diameter-2, center=true);
    }
}

// Assembly
union() {
    handle();
    tool_head();
    male_die();
    female_die();
    handle_grip();

    // Chamfers for printability
    color(color_main) {
        translate([0, 0, handle_length + head_height/2])
            rotate([90, 0, 0])
                cylinder(h=head_width, d=head_height, center=true);

        translate([0, 0, 0])
            rotate([90, 0, 0])
                cylinder(h=handle_diameter, d=handle_diameter, center=true);
    }
}

// Self-healing Cutting Mat Tool
// A realistic 3D printable cutting mat with measurement grid and corner radius

// Main dimensions
mat_length = 80;
mat_width = 60;
mat_thickness = 3;
corner_radius = 5;
wall_thickness = 1.5;

// Color definitions
mat_color = [0.1, 0.15, 0.05];  // Dark green self-healing mat
grid_color = [1, 1, 1];         // White grid lines
text_color = [0.8, 0.8, 0.8];   // Light gray text/numbers

// Create the main mat body
module cutting_mat() {
    difference() {
        // Main mat body with rounded corners
        translate([0, 0, -mat_thickness/2])
            minkowski() {
                cube([mat_length - 2*corner_radius, mat_width - 2*corner_radius, mat_thickness], center=true);
                cylinder(r=corner_radius, h=mat_thickness, $fn=20);
            }

        // Center cutting surface (slightly recessed)
        translate([0, 0, -0.5])
            cube([mat_length - 8, mat_width - 8, 1], center=true);
    }
}

// Create measurement grid lines
module grid_lines() {
    // Horizontal lines
    for (y = [-30:5:30]) {
        if (y >= -25 && y <= 25) {
            color(grid_color)
                translate([0, y, mat_thickness/2 - 0.1])
                    cube([mat_length - 2, 0.2, 0.2], center=true);
        }
    }

    // Vertical lines
    for (x = [-40:5:40]) {
        if (x >= -35 && x <= 35) {
            color(grid_color)
                translate([x, 0, mat_thickness/2 - 0.1])
                    cube([0.2, mat_width - 2, 0.2], center=true);
        }
    }

    // Bold 10mm lines
    for (x = [-40:10:40]) {
        if (x >= -30 && x <= 30) {
            color(grid_color)
                translate([x, 0, mat_thickness/2 - 0.1])
                    cube([0.4, mat_width - 2, 0.2], center=true);
        }
    }

    for (y = [-30:10:30]) {
        if (y >= -20 && y <= 20) {
            color(grid_color)
                translate([0, y, mat_thickness/2 - 0.1])
                    cube([mat_length - 2, 0.4, 0.2], center=true);
        }
    }
}

// Create corner angle guides
module angle_guides() {
    // 45 degree angle guides in corners
    for (i = [0:3]) {
        rotate([0, 0, i*90])
            translate([mat_length/2 - 10, mat_width/2 - 10, mat_thickness/2 - 0.1])
                color(grid_color)
                    cube([15, 0.2, 0.2]);
    }
}

// Create measurement markings (simplified as small cubes)
module measurements() {
    // Top edge numbers
    for (x = [-30:10:30]) {
        color(text_color)
            translate([x, mat_width/2 - 2, mat_thickness/2 - 0.1])
                cube([0.8, 0.8, 0.2], center=true);
    }

    // Left edge numbers
    for (y = [-20:10:20]) {
        color(text_color)
            translate([-mat_length/2 + 2, y, mat_thickness/2 - 0.1])
                cube([0.8, 0.8, 0.2], center=true);
    }
}

// Create non-slip feet
module feet() {
    foot_radius = 2;
    foot_height = 0.8;

    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x * (mat_length/2 - 10), y * (mat_width/2 - 8), -mat_thickness/2 - foot_height/2])
                cylinder(r=foot_radius, h=foot_height, $fn=16);
        }
    }
}

// Assembly
color(mat_color) cutting_mat();
grid_lines();
angle_guides();
measurements();
color([0.05, 0.05, 0.05]) feet();

// OpenSCAD code for a realistic medium-complexity pad tool
