// Metal striking pad
color("Silver") union() {
    // Main pad
    translate([32,0,4]) 
        rounded_cube([35,20,4], 2);

    // Reinforcement ridge
    translate([40,0,8]) 
        rounded_cube([19,16,2], 1);

    // Rivets
    for(pos=[[28,6,6],[28,-6,6],[52,6,6],[52,-6,6]]) {
