// Helper module for rounded cube
module rounded_cube(size, r) {
    hull() {
        for(dx=[r,size.x-r], dy=[r,size.y-r], dz=[r,size.z-r]) {
            translate([dx,dy,dz]) 
                sphere(r=r, $fn=16);
        }
    }
}

// Realistic Gloves Tool - Medium Complexity
// 3D printable work gloves for handling tools, approximately 85mm long

// Color constants
$fa = 2;
$fs = 0.5;

// Main glove module
module glove(is_right = true) {
    // Wrist cuff
    color([0.2, 0.2, 0.2]) 
    translate([0, 0, 0])
    cylinder(h=15, r1=15, r2=13, $fn=32);

    // Palm base
    color([0.3, 0.3, 0.3])
    translate([0, 0, 15])
    scale([1, 1.2, 0.8])
    sphere(r=12);

    // Thumb base
    color([0.25, 0.25, 0.25])
    translate([is_right ? 10 : -10, -5, 15])
    rotate([0, 0, is_right ? -30 : 30])
    scale([0.8, 1, 1.2])
    cylinder(h=20, r1=5, r2=4, $fn=16);

    // Thumb tip
    translate([is_right ? 15 : -15, -8, 32])
    scale([0.7, 0.9, 1.1])
    sphere(r=4);

    // Fingers (4 fingers)
    for(i = [0:3]) {
        finger(i, is_right);
    }

    // Knuckle guards
    for(i = [0:2]) {
        color([0.4, 0.4, 0.4])
        translate([is_right ? -5+i*5 : 5-i*5, 10, 22])
        scale([1.5, 0.5, 0.5])
        sphere(r=3);
