module finger(index, is_right) {
    // Position fingers in arc
    angle = -20 + index * 15;
    x_pos = is_right ? -8 + index * 6 : 8 - index * 6;

    // Finger base
    color([0.35, 0.35, 0.35])
    translate([x_pos, 12, 20])
    rotate([0, 0, is_right ? angle : -angle])
    scale([0.7, 0.8, 1.5])
    cylinder(h=15, r1=3, r2=2.5, $fn=12);

    // Finger middle segment
    translate([x_pos + sin(angle) * 12, 12 + cos(angle) * 12, 32])
    rotate([0, 0, is_right ? angle : -angle])
    scale([0.6, 0.7, 1.2])
    cylinder(h=12, r1=2.5, r2=2, $fn=12);

    // Finger tip
    translate([x_pos + sin(angle) * 20, 12 + cos(angle) * 20, 42])
    scale([0.5, 0.6, 1])
    sphere(r=2.5);
}

module grip_texture(is_right) {
    // Diamond pattern on palm
    for(x = [-8:4:8]) {
        for(y = [2:4:12]) {
            color([0.5, 0.5, 0.5])
            translate([is_right ? x : -x, y, 15 + 0.5])
            rotate([0, 0, 45])
            cube([2, 2, 0.5], center=true);
        }
    }
}

