                rotate([0, 0, 180])
                cylinder(h=jack_height+1, r=heel_width/4, $fn=20);
            translate([jack_length/2 - heel_gap/2, -heel_width/2, -jack_height/2])
                cylinder(h=jack_height+1, r=heel_width/4, $fn=20);
