module nozzle_body() {
    color("silver")
    union() {
        // Main cylindrical body
        cylinder(h=nozzle_length - tip_length, d=nozzle_diameter, center=false);

        // Tapered tip
        translate([0, 0, nozzle_length - tip_length])
        cylinder(h=tip_length, d1=nozzle_diameter, d2=nozzle_diameter/3, center=false);

        // Internal bore
        difference() {
            cylinder(h=nozzle_length, d=nozzle_diameter - wall_thickness*2, center=false);
            translate([0, 0, -0.1])
            cylinder(h=nozzle_length + 0.2, d=nozzle_diameter - wall_thickness*2, center=false);
