            cylinder(h=5, d1=handle_diameter, d2=handle_diameter + 5, center=false);

            // Connection to nozzle
            translate([0, 0, -handle_length])
            cylinder(h=wall_thickness*2, d=nozzle_diameter + wall_thickness*2, center=false);
