colorKnob  = "FireBrick";
colorDial  = "White";
colorHands = "Black";

module timer() {
    scale([scaleFactor,scaleFactor,scaleFactor]) {

        /* ---------- Body / bell housing ---------- */
        difference() {
            // Main hemispherical shell
            color(colorBody)
            sphere(d = 70);

            // Cut off bottom so it sits flat
            translate([0,0,-35])
                cube([100,100,40], center=true);

            // Sound slots
            for(a = [0:60:359])
                rotate([0,0,a])
                translate([0,34,-20])
                    cube([2,8,40], center=true);
        }

        // Cylindrical base ring for stable standing
        color(colorBody)
        translate([0,0,-35])
        cylinder(h = 8, d = 75);

        /* ---------- Mechanical mechanism housing ---------- */
        translate([0,0,0])
        difference() {
            // Central column
            cylinder(h = 40, d = 18);

            // Top hole for winding shaft
            translate([0,0,30])
                cylinder(h = 15, d = 6);
        }

        /* ---------- Wind-up knob ---------- */
        translate([0,0,35])
        color(colorKnob)
        union() {
            cylinder(h = 10, d = 22);
            // Grip ridges
            for(a = [0:45:359])
                rotate([0,0,a])
                translate([9.5,0,5])
                    cube([4,2,10], center=true);
        }

        /* ---------- Dial face ---------- */
        translate([0,0,15])
        color(colorDial)
        cylinder(h = 1.2, d = 55);

        // Minute markings
        for(m = [0:5:55])
            rotate([0,0,m*6])
            translate([0,23.5,16.2])
                cube([0.8,2.5,1.2], center=true);

        // Numbers 00-55
        for(m = [0:5:55])
            rotate([0,0,m*6])
            translate([0,19,16.3])
                text(str(m/5*5,2), size=3, h=0.4, center=true);

        /* ---------- Timer hand ---------- */
        rotate([0,0,270])  // set to zero position
        translate([0,0,16.5])
        color(colorHands)
        union() {
            // Pointer
            cube([2,19,1], center=true);
            // Hub
            cylinder(h = 2.5, d = 6);
        }

        /* ---------- Carry handle ---------- */
        color(colorBody)
        translate([0,0,35]) {
            difference() {
                union() {
                    // Arc
                    rotate_extrude(angle = 180, $fn = 64)
                        translate([35,0,0])
                            circle(d = 6);
                    // Side supports
                    translate([0,-35,0])
                        rotate([90,0,0])
                            cylinder(h = 10, d = 6);
                    translate([0, 35,0])
                        rotate([90,0,0])
                            cylinder(h = 10, d = 6);
                }
                // Make handle hollow for printing
                rotate_extrude(angle = 180, $fn = 64)
                    translate([35,0,0])
                        circle(d = 3);
            }
        }
    }
}

