    translate([scope_length/2 + 6, 0, 0])
    rotate([0, 90, 0])
    difference() {
        cylinder(h = 8, d = eyepiece_diameter);
        translate([0, 0, -1])
        cylinder(h = 10, d = eyepiece_diameter - 6);
    }
