// Adjustment turrets
module adjustment_turret(height, angle) {
    color(color_adjust)
    rotate([0, 90, angle])
    translate([0, 0, scope_diameter/2 + height/2])
    difference() {
        cylinder(h = height, d = 8);
        translate([0, 0, -1])
        cylinder(h = height + 2, d = 6);
    }
}

// Windage and elevation knobs
module adjustment_knobs() {
    // Windage knob (left side)
    translate([-scope_length/4, -scope_diameter/2 - 5, 0])
    rotate([90, 0, 0])
    cylinder(h = 4, d = 6);

    // Elevation knob (top)
    translate([0, 0, scope_diameter/2 + 5])
    rotate([0, 90, 0])
    cylinder(h = 4, d = 6);
}

// Grip rings
module grip_rings() {
    color(color_grip)
    for(i = [-1, 0, 1]) {
        translate([i * 15, 0, 0])
        difference() {
            cylinder(h = 5, d = scope_diameter + 4);
            cylinder(h = 7, d = scope_diameter);
        }
    }
}

// Mounting rail
module mounting_rail() {
    color(color_body)
    translate([0, 0, -scope_diameter/2 - 3])
    cube([scope_length + 20, 8, 3], center = true);

    // Rail slots
    for(i = [-3:1:3]) {
        translate([i * 8, 0, -scope_diameter/2 - 1.5])
        cube([2, 6, 3], center = true);
    }
}

// Lens caps
module lens_cap(position) {
    color(color_body)
    translate([position, 0, 0])
    rotate([0, 90, 0])
    difference() {
        cylinder(h = 4, d = objective_diameter + 2);
        cylinder(h = 5, d = objective_diameter - 2);
    }
}

