head_height = 5;
wheel_diameter = 5;
wheel_thickness = 1.5;
shaft_diameter = 6;
shaft_length = 15;

// Main assembly
module glass_cutter() {
    color(cutter_color) {
        // Handle with grip texture
        union() {
            // Main handle body
            translate([0, 0, handle_length/2])
                cylinder(h=handle_length, d=handle_diameter, $fn=32);

            // Textured grip section
            color(handle_color)
            translate([0, 0, handle_length - grip_length/2])
                cylinder(h=grip_length, d=grip_diameter, $fn=32);

            // Grip rings
            for(i = [0:4]) {
                color(accent_color)
                translate([0, 0, handle_length - grip_length + i*10])
                    cylinder(h=2, d=grip_diameter + 1, $fn=32);
            }
        }
    }

    // Cutting head assembly
    color(cutter_color) {
        translate([0, 0, handle_length + head_length/2])
        rotate([90, 0, 0])
        difference() {
            // Main head body
            cube([head_width, head_length, head_height], center=true);

            // Wheel cavity
            translate([0, head_length/2 - wheel_diameter/2 - 1, 0])
                cube([wheel_diameter + 2, wheel_diameter + 2, head_height + 1], center=true);

            // Shaft hole
            translate([0, -head_length/2 + shaft_length/2, 0])
                rotate([0, 90, 0])
                cylinder(h=head_width + 2, d=shaft_diameter, center=true, $fn=16);
        }
    }

    // Cutting wheel
    color(wheel_color)
    translate([0, handle_length + head_length - wheel_diameter/2 - 1, wheel_thickness/2])
    rotate([90, 0, 0])
    cylinder(h=wheel_thickness, d=wheel_diameter, $fn=32);

    // Shaft
    color(cutter_color)
    translate([0, handle_length + head_length/2 - shaft_length/2, 0])
    rotate([90, 0, 0])
    cylinder(h=shaft_length, d=shaft_diameter, $fn=16);

    // Alignment marks
    for(i = [0:2]) {
        color(accent_color)
        translate([0, handle_length + head_length - 5 - i*5, head_height/2 + 0.5])
            cube([0.5, 3, 0.3], center=true);
    }
}

// Render the glass cutter
