// Wooden handle with ergonomic grip
module handle() {
    color(color_handle)
    union() {
        // Main handle shaft
        cylinder(h=handle_length, d=handle_diameter, center=false);

        // Grip rings for better handling
        for(i=[0:5:handle_length-5]) {
            if(i > 10 && i < handle_length - grip_length) {
                translate([0, 0, i])
                difference() {
                    cylinder(h=3, d=handle_diameter + 2, center=false);
                    cylinder(h=4, d=handle_diameter - 1, center=true);
                }
            }
        }

        // End cap
        translate([0, 0, handle_length])
        sphere(d=handle_diameter + 2);
    }
}

// Rubber grip section
module rubber_grip() {
    color(color_grip)
    translate([0, 0, handle_length - grip_length])
    difference() {
        cylinder(h=grip_length, d=handle_diameter + 3, center=false);

        // Grip texture grooves
        for(i=[0:2:grip_length-2]) {
            translate([0, 0, i])
            cylinder(h=1, d=handle_diameter + 1, center=false);
        }
    }
}

// Snuffer cone with ventilation holes
module snuffer_cone() {
    color(color_cone)
    union() {
        // Main cone body
        cylinder(h1=cone_height, h2=0, r1=cone_base_diameter/2, r2=cone_top_diameter/2, center=false);

        // Reinforcement ring at base
        translate([0, 0, 0])
        cylinder(h=3, d=cone_base_diameter + 4, center=false);

        // Ventilation holes for smoke escape
        for(angle=[0:30:330]) {
            rotate([0, 0, angle])
            translate([cone_base_diameter/2 - 2, 0, cone_height/2])
            rotate([90, 0, 0])
            cylinder(h=cone_height, d=2, center=true);
        }

        // Hinge connection point
        translate([0, 0, -5])
        rotate([90, 0, 0])
        union() {
            cylinder(h=hinge_length, d=6, center=true);
            sphere(d=6);
        }
    }
}

// Assembly
translate([0, 0, 0])
union() {
    handle();
    rubber_grip();

    translate([0, 0, 0])
    rotate([90, 0, 0])
    translate([0, handle_diameter/2, 0])
    snuffer_cone();
}

