// Realistic 3D-printable spring paper-clip tool (medium complexity, ~120 lines)
// Overall size: ~70 mm long, 30 mm wide, 20 mm thick

// ---- Parameters ----------------------------------------------------------
$fn = 36;                // Resolution for curves
wall = 1.8;              // Wall thickness for 3D printing
s = 1.05;                // Small tolerance for moving parts
grip_d = 7;              // Handle grip diameter
clip_l = 35;             // Actual clip jaw length
spring_od = 8;           // Coil spring outer diameter
spring_w = 1.2;          // Spring wire diameter

// ---- Main assembly -------------------------------------------------------
module clip_tool() {
    color("silver")
    union() {
        left_handle();
        right_handle();
        coil_spring();
    }
}

// ---- Left handle ---------------------------------------------------------
module left_handle() {
    difference() {
        // Main handle
        translate([0, 0, 0])
            handle_body();

        // Finger grooves on grip
        for (i = [-1:1])
            translate([0, -12 + i*5, 0])
                rotate([90, 0, 0])
                    cylinder(h=10, d=grip_d*0.9, $fn=12);

        // Jaw inner bevel
        translate([clip_l, 0, 0])
            rotate([0, 0, 20])
                cube([clip_l, 50, 50], center=true);
    }
}

// ---- Right handle (mirrored) ---------------------------------------------
module right_handle() {
    mirror([0, 1, 0])
        left_handle();
}

// ---- Handle body ---------------------------------------------------------
module handle_body() {
    union() {
        // Grip
        translate([-clip_l/2, -15, 0])
            rotate([0, 90, 0])
                cylinder(h=30, d=grip_d);

        // Arm
        translate([clip_l/2, 0, 0])
            cube([clip_l, 10, 5], center=true);

        // Reinforcement rib
        translate([clip_l*0.75, 0, -2.5])
            cube([clip_l/2, 8, 2.5]);
    }
}

// ---- Coil spring ---------------------------------------------------------
module coil_spring() {
    // Central pivot cylinder
    translate([0, 0, 0])
        cylinder(h=wall*2, d=4);

    // Spring coils
    for (i = [0:359])
        rotate([0, 0, i])
            translate([spring_od/2, 0, 0])
                rotate([90, 0, 0])
                    cylinder(h=spring_w*1.2, d=spring_w);
}

// ---- Render --------------------------------------------------------------
clip_tool();

