        cylinder(h = handle_length, d = handle_diameter);

        // Grip rings
        color(grip_color) {
            for(i = [0:6:handle_length-10]) {
                translate([0, 0, i+5]) {
                    difference() {
                        cylinder(h = 3, d = handle_diameter + 2);
                        cylinder(h = 3.5, d = handle_diameter - 2);
                    }
                }
            }
        }
    }
}

// Tie mechanism module
module tie_mechanism() {
    color(tie_color) {
        // Main tie body
        translate([0, 0, tie_thickness/2]) {
            cube([tie_width, tie_length, tie_thickness], center = true);
        }

        // Tapered end
        translate([0, tie_length/2 + 5, tie_thickness/2]) {
            scale([0.7, 1, 1]) {
                cube([tie_width, 10, tie_thickness], center = true);
            }
        }

        // Tie adjustment slots
        for(i = [1:3]) {
            translate([0, -tie_length/2 + i*15, tie_thickness/2]) {
                cube([tie_width * 0.6, 2, tie_thickness + 0.5], center = true);
            }
        }
    }

    // Metal adjustment mechanism
    color(metal_color) {
        // Top plate
        translate([0, tie_length/4, tie_thickness + 1]) {
            cube([tie_width * 0.8, 20, 1], center = true);
        }

        // Bottom plate
        translate([0, -tie_length/4, -1]) {
            cube([tie_width * 0.8, 20, 1], center = true);
        }

        // Side rails
        for(side = [-1, 1]) {
            translate([side * (tie_width/2 + 1), 0, tie_thickness/2]) {
                cube([2, tie_length + 10, tie_thickness], center = true);
            }
        }
    }
}

// Assembly
module tie_tool() {
    // Handle
    translate([0, -handle_length/2 - tie_length/2 - 5, 0]) {
        rotate([0, 0, 0]) {
            handle();
        }
    }

    // Connection piece
    color(metal_color) {
        translate([0, -tie_length/2 - 5, 0]) {
            rotate([90, 0, 0]) {
                cylinder(h = 10, d = handle_diameter - 2);
            }
        }
    }

    // Tie mechanism
    translate([0, 0, 0]) {
        rotate([0, 0, 0]) {
            tie_mechanism();
        }
    }

    // Reinforcement ribs
    color(tie_color) {
        for(pos = [-tie_width/4, tie_width/4]) {
            translate([pos, 0, tie_thickness + 2]) {
                cube([3, tie_length * 0.8, 2], center = true);
            }
        }
    }
}

