            cylinder(h=5, d=handle_diameter + 2, $fn=32);
    }
}

// Cord spool mechanism
module spool() {
    color(core_color) {
        // Central core
        cylinder(h=spool_width, d=core_diameter, $fn=32);

        // End flanges
        for(side = [-1, 1]) {
            translate([0, 0, side * (spool_width/2 + wall_thickness)])
                cylinder(h=wall_thickness, d=core_diameter + 15, $fn=32);
        }

        // Cord entry slot
