            rotate([90, 0, 0])
                translate([0, 0, 6])
                    cube([5, 0.5, 3], center=true);
    }
}

// Cord guide mechanism
module cord_guide() {
    color(metal_color) {
        // Guide arm
        translate([0, -handle_diameter/2 - 10, handle_length/2])
            rotate([0, 90, 0])
                difference() {
                    cylinder(h=20, d=6, $fn=24);
                    translate([0, 0, -1])
                        cube([4, 4, 22], center=true);
                }

