// Main assembly
module rope_tool() {
    difference() {
        union() {
            // Handle with grip texture
            color(handle_color)
            translate([0, 0, 0])
                handle_with_grip();

            // Blade with rope channel
            color(blade_color)
            translate([handle_length - 5, 0, 0])
                blade_with_channel();

            // Reinforcement between handle and blade
            color(handle_color)
            translate([handle_length - 8, 0, 0])
                reinforcement();
        }

        // Remove cutting edge chamfer
        translate([handle_length + blade_length - 2, 0, -1])
            rotate([0, 45, 0])
            cube([5, blade_width + 2, 10]);
    }
}

// Handle with grip texture
module handle_with_grip() {
    difference() {
        // Main handle cylinder
        cylinder(h = handle_length, d = handle_diameter, $fn = 32);

        // Grip rings
        for(i = [5:8:handle_length - 10]) {
            translate([0, 0, i])
                cylinder(h = grip_depth, d = handle_diameter + 0.5, $fn = 32);
        }
    }

    // Grip texture dots
    color(grip_color)
    for(i = [8:8:handle_length - 10]) {
        rotate([0, 0, 0])
        for(j = [0:30:330]) {
            rotate([0, 0, j])
            translate([handle_diameter/2 - 0.5, 0, i + grip_depth/2])
                sphere(d = 1.5, $fn = 8);
        }
    }
}

// Blade with rope channel
module blade_with_channel() {
    difference() {
        // Main blade
        cube([blade_length, blade_width, blade_thickness]);

        // Rope channel
        translate([blade_length/2, 0, blade_thickness/2])
            rotate([90, 0, 0])
            cylinder(h = blade_width + 2, r = channel_radius, $fn = 32);

        // Secondary guide channel
        translate([blade_length/2 - 10, 0, blade_thickness/2])
            rotate([90, 0, 0])
            cylinder(h = blade_width/2, r = channel_radius - 1, $fn = 32);
    }

    // Cutting edge indicator
    color([0.9, 0.9, 0.9])
    translate([blade_length - 1, 0, blade_thickness/2])
        cube([0.5, blade_width, blade_thickness]);
}

// Reinforcement between handle and blade
module reinforcement() {
    hull() {
        translate([0, -handle_diameter/2, 0])
            cube([5, handle_diameter, 5]);
        translate([0, -blade_width/2, -2])
            cube([5, blade_width, 2]);
    }
}

