// Main stool assembly
module stool() {
    // Seat with tool storage cavity
    union() {
        difference() {
            // Main seat
            color(seat_color)
            cylinder(h=8, d=seat_diameter+4, $fn=60);

            // Tool storage cavity
            translate([0, 0, -1])
            cylinder(h=10, d=seat_diameter-10, $fn=60);

