                rotate([90, 0, 0])
                cylinder(h=8, d=3, $fn=12);
            }
        }

        // Reinforcement ring
        color(leg_color)
        translate([0, 0, 2])
        cylinder(h=2, d=seat_diameter+2, $fn=60);
    }

    // Leg assembly
    legs();

    // Folding mechanism
    mechanisms();

    // Tool features
    tool_features();
